import java.util.Scanner;

public class Main {
    public static void main(String[] args){
    	int[][] ink = new int[10][10];
    	Scanner input = new Scanner(System.in);
		while(input.hasNext()){
			String[] st = input.next().split(",");
			int x = Integer.parseInt(st[0]);
			int y = Integer.parseInt(st[1]);
			if(st[2].charAt(0) == '1'){
				ink[y][x]++;
				if(0 <= x-1)ink[y][x-1]++;
				if(0 <= y-1)ink[y-1][x]++;
				if(x+1 < 10)ink[y][x+1]++;
				if(y+1 < 10)ink[y+1][x]++;
			}else if(st[2].charAt(0) == '2'){
				for(int i = y-1 ; i <= y+1 ; i++){
					for(int j = x-1 ; j <= x+1 ;j++){
						if(isok(j,i))ink[i][j]++;
					}
				}
			}else{
				for(int i = y-2 ; i <= y+2 ; i++){
					for(int j = x-2 ; j <= x+2 ;j++){
						if(isok(j,i) && Math.abs(x-j)+Math.abs(y-i) <= 2)ink[i][j]++;
					}
				}
			}
		}
		int maxink = 0;
		int count = 0;
		for(int i = 0 ; i < 10 ; i++){
			for(int j = 0 ; j < 10 ; j++){
				if(ink[i][j] == 0)count++;
				maxink = Math.max(maxink, ink[i][j]);
			}
		}
		System.out.println(count);
		System.out.println(maxink);
    }

    public static boolean isok(int x,int y){
    	if(x < 0 || y < 0 || x >= 10 || y >= 10)return false;
    	return true;
    }

}

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

public class Main{
	public static void main(String[] args) throws IOException {
		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
		String str;
		int blocks[][] = new int[10][10];
		int blank=0,max=0;

		while((str=br.readLine()) != null){
			String strArray[] = str.split(",");
			int x = Integer.parseInt(strArray[0]);
			int y = Integer.parseInt(strArray[1]);
			int size = Integer.parseInt(strArray[2]);

			drop(blocks, x, y, size);

		}

		for(int i=0; i<10; i++){
			for(int j=0; j<10; j++){
				if(blocks[i][j]==0){
					blank++;
				}else if(blocks[i][j]>max){
					max = blocks[i][j];
				}
			}
		}

		System.out.println(blank);
		System.out.println(max);

	}

	static void drop(int blocks[][],int x, int y, int size){
		if(size == 1)//
			min_drop(blocks, x, y);
		if(size == 2)
			mid_drop(blocks, x, y);
		if(size == 3)
			lag_drop(blocks, x, y);
	}

	static void min_drop(int blocks[][], int x, int y){
		blocks[x][y]++;
		blocks[x-1][y]++;
		blocks[x+1][y]++;
		blocks[x][y-1]++;
		blocks[x][y+1]++;
	}

	static void mid_drop(int blocks[][], int x, int y){
		min_drop(blocks, x, y);
		blocks[x-1][y-1]++;
		blocks[x+1][y-1]++;
		blocks[x-1][y+1]++;
		blocks[x+1][y+1]++;
	}

	static void lag_drop(int blocks[][], int x, int y){
		mid_drop(blocks, x, y);
		blocks[x][y-2]++;
		blocks[x][y+2]++;
		blocks[x-2][y]++;
		blocks[x+2][y]++;
	}

}

import java.util.Scanner;

public class Main {
 static int     max   = 0;
 static int     count = 100;
 static int[][] p     = new int[10][10];

 public static void main(String[] args) {
	Scanner in = new Scanner(System.in)
	  .useDelimiter("[^0-9]+");
	max = 0;
	count = 100;
	for (int i = 0; i < p.length; i++) {
	 for (int j = 0; j < p[i].length; j++) {
		p[i][j] = 0;
	 }
	}
	while (in.hasNextLine()) {
	 ink(in.nextInt(), in.nextInt(), in.nextInt());
	 System.out.println(count);
	 System.out.println(max);
	}

 }

 static void ink(int x, int y, int size) {
	if (size >= 3) {
	 pi(x + 2, y);
	 pi(x - 2, y);
	 pi(x, y + 2);
	 pi(x, y - 2);
	}
	if (size >= 2) {
	 pi(x + 1, y + 1);
	 pi(x + 1, y - 1);
	 pi(x - 1, y + 1);
	 pi(x - 1, y - 1);
	}
	if (size >= 1) {
	 pi(x + 1, y);
	 pi(x - 1, y);
	 pi(x, y + 1);
	 pi(x, y - 1);
	}
	pi(x, y);
 }

 static void pi(int x, int y) {
	if (0 <= x && x < 10 && 0 <= y && y < 10) {
	 if (p[x][y] == 0) {
		count--;
	 }
	 p[x][y]++;
	 max = Math.max(max, p[x][y]);
	}
 }
}

