		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
		String str;
		int board[][] = new int[10][10];
		while((str = br.readLine()) != null) {
			String[] nstr = str.split(",");
			int n[] = new int[]{Integer.parseInt(nstr[0]), Integer.parseInt(nstr[1]), Integer.parseInt(nstr[2])};

			if(n[2] == 2) {
				for(int i = -1; i <= 1; i++) {
					if(n[0] + i < 0) continue;
					for(int j = -1; j <= 1; j++) {
						if(n[1] + j < 0) continue;
						board[n[0] + i][n[1] + j]++;
					}
				}
			} else {
				for(int i = 0; i < 10; i++) {
					for(int j = 0; j < 10; j++) {
						if(n[2] == 1) {
							if(Math.abs((n[0] - i) + (n[1] - j)) <= 1) {
								board[i][j]++;
							}
						} else {
							if(Math.abs((n[0] - i) + (n[1] - j)) <= 2) {
								board[i][j]++;
							}
						}
					}
				}
			}
		}

		int cnt = 0, max = 0;
		for(int i = 0; i < 10; i++) {
			for(int j = 0; j < 10; j++) {
				if(board[i][j] == 0) cnt++;
				if(board[i][j] > max) max = board[i][j];
			}
		}
		System.out.println(cnt);
		System.out.println(max);
	}
}

import java.util.*;

public class Main {

    public static void main (String[] args) {
        Scanner scanner = new Scanner(System.in);
        int[][] paper = new int[10][10];
        int max = 0;
        int counter = 0;

        for (int i = 0; i < paper.length; i++) {
            for (int j = 0; j < paper[i].length; j++) {
                paper[i][j] = 0;
            }
        }

            String strLine = scanner.next();
            String[] str = strLine.split(",");

            int x = Integer.parseInt(str[0]);
            int y = Integer.parseInt(str[1]);
            int size = Integer.parseInt(str[2]);

            paper[x][y]++;
            if (isInsidePaper(x, y-1)) paper[x][y-1]++;
            if (isInsidePaper(x-1, y)) paper[x-1][y]++;
            if (isInsidePaper(x+1, y)) paper[x+1][y]++;
            if (isInsidePaper(x, y+1)) paper[x][y+1]++;

            if (size >= 2) {
                if (isInsidePaper(x-1, y-1))    paper[x-1][y-1]++;
                if (isInsidePaper(x+1, y-1))    paper[x+1][y-1]++;
                if (isInsidePaper(x-1, y+1))    paper[x-1][y+1]++;
                if (isInsidePaper(x+1, y+1))    paper[x+1][y+1]++;
            }
            if (size == 3) {
                if (isInsidePaper(x, y-2))    paper[x][y-2]++;
                if (isInsidePaper(x-2, y))    paper[x-2][y]++;
                if (isInsidePaper(x+2, y))    paper[x-2][y]++;
                if (isInsidePaper(x, y+2))    paper[x][y+2]++;
            }

            for (int i = 0; i < paper.length; i++) {
            for (int j = 0; j < paper[i].length; j++) {
                if (paper[i][j] == 0) {
                    counter++;
                } else {
                    if (paper[i][j] > max) max = paper[i][j];
                }
            }
        }
        System.out.println(counter);
        System.out.println(max);
    }

    public static boolean isInsidePaper(int x, int y) {
        if (x >= 0 && x <= 9 && y >= 0 && y <= 9) {
            return true;
        } else {
            return false;
        }
    }
}

import java.util.*;

class Main {

    static int [][] paper;

    static void pour (int x, int y, int size) {
        paper[x][y] += 1;
        if (x > 0)  paper[x-1][y] += 1;
        if (x < 9)  paper[x+1][y] += 1;
        if (y > 0)  paper[x][y-1] += 1;
        if (y < 9)  paper[x][y+1] += 1;

        if (size >= 2) {
            if (x > 0 && y > 0) paper[x-1][y-1] += 1;
            if (x > 0 && y < 9) paper[x-1][y+1] += 1;
            if (x < 9 && y > 0) paper[x+1][y-1] += 1;
            if (x < 9 && y < 9) paper[x+1][y+1] += 1;
        }

        if (size == 3) {
            if (x > 1)  paper[x-2][y] += 1;
            if (x < 8)  paper[x+2][y] += 1;
            if (y > 1)  paper[x][y-2] += 1;
            if (y < 8)  paper[x][y+2] += 1;
        }
    }

    static void calculate () {
        int max = 0;
        int empty = 0;

        for(int i = 0; i < 10; i++) {
            for (int j = 0; j < 10; j++) {
                if (paper[i][j] == 0) {
                    empty += 1;
                } else if (paper[i][j] > max) {
                    max = paper[i][j];
                }
            }
        }

        System.out.println(empty);
        System.out.println(max);
    }

    public static void main (String [] args) {

        paper = new int[10][10];

        Scanner sc = new Scanner(System.in);

        while(sc.hasNext()) {
            String [] line = sc.nextLine().split(",");
            int x = Integer.parseInt(line[0]);
            int y = Integer.parseInt(line[1]);
            int size = Integer.parseInt(line[2]);

            pour(x, y, size);
        }

        calculate();

    }
}

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;
import java.util.Scanner;

public class AOJ0026DroppingInk {

    public static void main(String[] args) {

        exec();
    }

    private static void exec() {

        Scanner sc = new Scanner(System.in);
        List<String> mapPointList = new ArrayList<String>();
        int[][] dropPoint = null; // 

        while (sc.hasNextInt()) {

            int x = sc.nextInt(); // x
            int y = sc.nextInt(); // y
            int size = sc.nextInt(); // 

            // 
            if (size == 1) {
                dropPoint = smallDrop(x, y);
            } else if (size == 2) {
                dropPoint = middleDrop(x, y);
            } else if (size == 3) {
                dropPoint = largeDrop(x, y);
            }

            // 
            mapPointList = writePointMapArray(mapPointList, dropPoint);

            // 
            int[] result = writeDeepPoint(mapPointList);

            System.out.println(result[0]);
            System.out.println(result[1]);
        }
    }

    private static List<String> writePointMapArray(List<String> mapPointList, int[][] pointArray) {

        int index = 0;

        for (int point : pointArray[0]) {

            if ((point >= 0 && point < 10)
                && (pointArray[1][index] >= 0 && pointArray[1][index] < 10)) {

                String mapPoint = String.valueOf(point) + String.valueOf(pointArray[1][index]);
                mapPointList.add(mapPoint);
            }

            index++;
        }

        return mapPointList;
    }

    private static int[] writeDeepPoint(List<String> mapPointList) {

        Map<String, Integer> pointMap = new HashMap<String, Integer>();
        int deepPoint = 1;

        // 
        for (String point : mapPointList) {

            if (pointMap.containsKey(point)) {

                Integer number = pointMap.get(point);

                pointMap.remove(point);
                pointMap.put(point, number + 1);

                if (deepPoint < pointMap.get(point)) {

                    deepPoint = pointMap.get(point);
                }

            } else {

                pointMap.put(point, 1);
            }
        }

        // 
        int whitePoint = 100 - pointMap.size();

        int[] result = new int[] { whitePoint, deepPoint };

        return result;
    }

    private static int[][] largeDrop(int x, int y) {

        int[][] largeDropPointArray;
        largeDropPointArray = new int[2][];

        largeDropPointArray[0] =
            new int[] { x, x - 1, x, x + 1, x - 2, x - 1, x, x + 1, x + 2, x - 1, x, x + 1, x };
        largeDropPointArray[1] =
            new int[] { y - 2, y - 1, y - 1, y - 1, y, y, y, y, y, y + 1, y + 1, y + 1, y + 2 };

        return largeDropPointArray;
    }

    private static int[][] middleDrop(int x, int y) {

        int[][] middleDropPointArray;
        middleDropPointArray = new int[2][];

        middleDropPointArray[0] = new int[] { x - 1, x, x + 1, x - 1, x, x + 1, x - 1, x, x + 1 };
        middleDropPointArray[1] = new int[] { y - 1, y - 1, y - 1, y, y, y, y + 1, y + 1, y + 1 };

        return middleDropPointArray;
    }

    private static int[][] smallDrop(int x, int y) {

        int[][] smallDropPointArray;
        smallDropPointArray = new int[2][];

        smallDropPointArray[0] = new int[] { x, x - 1, x, x + 1, x };
        smallDropPointArray[1] = new int[] { y - 1, y, y, y, y + 1 };

        return smallDropPointArray;
    }
}

