				map[x[0]][x[1]]++;
				map[x[0]][x[1]-1]++;
				map[x[0]][x[1]+1]++;
				map[x[0]-1][x[1]]++;
				map[x[0]+1][x[1]]++;
				}
			if(x[2] >= 2){
				map[x[0]-1][x[1]-1]++;
				map[x[0]+1][x[1]-1]++;
				map[x[0]-1][x[1]+1]++;
				map[x[0]+1][x[1]+1]++;
			}
			if(x[2] == 3){
				map[x[0]][x[1]-2]++;
				map[x[0]][x[1]+2]++;
				map[x[0]+2][x[1]]++;
				map[x[0]-2][x[1]]++;
			}
		}
