public class Main{
	public static void main(String[] args)throws IOException{
		int[][] map = new int[9][9];
		BufferedReader br = null;
		int max = 0,num = 0;
		try{
			br = new BufferedReader(new InputStreamReader(System.in));
			String buf;
			while((buf = br.readLine())!=null){
				String[] data = buf.split(",");
				int x = new Integer(data[0]);
				int y = new Integer(data[1]);
				int point = new Integer(data[2]);
				switch(point){
				case 3:
					for(int i = -2; i <=2; i+=4){
							if(check(x+i ,y))
									map[x+i][y]++;
							if(check(x,y+i))
								map[x][y+i]++;
					}
				case 2:
					for(int i = -1 ; i <=1; i+=2){
						for(int j = -1 ; j <=1 ;j +=2){
							if(check(x+i,y+j))
								map[x+i][y+j]++;
						}
					}
				case 1:
					for(int i = -1; i <=1; i+=2){
						if(check(x+i ,y))
								map[x+i][y]++;
						if(check(x,y+i))
							map[x][y+i]++;
					}
					map[x][y]++;
				}

				for(int i = 0; i < map.length;i++){
					for(int j = 0; j < map[i].length;j++){
						if(map[i][j] == 0)num++;
						max = Math.max(max,map[i][j]);
					}
				}
			}

		}finally{
			br.close();
		}
		System.out.printf("%d\n%d\n",num,max);
	}
		static boolean check(int x , int y){
			if(x > 9 || x < 0 || y > 9 || y < 0)return false;
			return true;
		}
}

import java.util.Scanner;

public class Main{

    public static void main(String[] args) {
	Scanner sc = new Scanner(System.in);

	int[][] tiles = new int[10][10];

	while (sc.hasNextLine()) {
	    String[] input = sc.nextLine().split(",");

	    if ("".equals(input[0])) {
		break;
	    }
	    int x = Integer.parseInt(input[0]);
	    int y = Integer.parseInt(input[1]);
	    int size = Integer.parseInt(input[2]);

	    if (1 == size) {
		for (int i = -1; i < 2; i++) {
		    if (i == 0) {
			if (compBet(y - 1)) {
			    tiles[x][y - 1]++;
			}

			if (compBet(y + 1)) {
			    tiles[x][y + 1]++;
			}
		    }

		    if (compBet(x + i)) {
			tiles[x + i][y]++;
		    }

		}
	    }

	    if (2 == size) {
		for (int i = -1; i < 2; i++) {
		    for (int j = -1; j < 2; j++) {
			if (compBet(x + i) && compBet(y + j)) {
			    tiles[x + i][y + j]++;
			}
		    }
		}
	    }

	    if (3 == size) {
		for (int i = -1; i < 2; i++) {
		    for (int j = -1; j < 2; j++) {
			if (compBet(x + i) && compBet(y + j)) {
			    tiles[x + i][y + j]++;
			}
		    }
		}
		if (compBet(x - 2)) {
		    tiles[x - 2][y]++;
		}

		if (compBet(x + 2)) {
		    tiles[x + 2][y]++;
		}

		if (compBet(y - 2)) {
		    tiles[x][y - 2]++;
		}

		if (compBet(y + 2)) {
		    tiles[x][y + 2]++;
		}

	    }

	}
	System.out.println(countZero(tiles));
	System.out.println(countMax(tiles));

    }

    private static boolean compBet(int xPlusI) {
	if (xPlusI <= -1) {
	    return false;
	}

	if (xPlusI >= 10) {
	    return false;
	}

	return true;
    }

    private static int countMax(int[][] tiles) {
	int max = 0;

	for (int[] yLine : tiles) {
	    for (int tile : yLine) {
		max = Math.max(max, tile);
	    }
	}
	return max;
    }

    private static int countZero(int[][] tiles) {
	int blankCount = 0;

	for (int[] yLine : tiles) {
	    for (int tile : yLine) {
		if (tile == 0) {
		    blankCount++;
		}
	    }
	}

	return blankCount;
    }
}

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

public class Main {

	private static int ink[][] = new int[10][10];
	private static String line[] = new String[3];
	private static int clearCells = 0;
	private static int mostDeepCell = 0;

	public static void main(String[] args) throws NumberFormatException,
			IOException {

		InputStreamReader isr = new InputStreamReader(System.in);
		BufferedReader br = new BufferedReader(isr);

		String str = null;
		while ((str = br.readLine()) != null) {

			line = str.split(",", 3);

			int x = Integer.parseInt(line[0]);
			int y = Integer.parseInt(line[1]);
			int size = Integer.parseInt(line[2]);

			if (size == 1) {
				small(x, y);
			}

			if (size == 2) {
				medium(x, y);
			}

			if (size == 3) {
				large(x, y);
			}
			for (int i = 0; i < 10; ++i) {
				for (int j = 0; j < 10; ++j) {

					if (ink[i][j] == 0) {
						clearCells++;
					}

					if (mostDeepCell < ink[i][j]) {
						mostDeepCell = ink[i][j];
					}
				}
			}

			System.out.println(clearCells);
			System.out.println(mostDeepCell);

		}
	}

	private static void small(int x, int y) {

		ink[x][y]++;

		if (0 <= x - 1) {
			ink[x - 1][y]++;
		}

		if (x + 1 < 10) {
			ink[x + 1][y]++;
		}

		if (0 <= y - 1) {
			ink[x][y - 1]++;
		}

		if (y + 1 < 10) {
			ink[x][y + 1]++;
		}
	}

	private static void medium(int x, int y) {

		small(x, y);

		if (0 <= x - 1 && 0 <= y - 1) {
			ink[x - 1][y - 1]++;
		}

		if (0 <= x - 1 && y + 1 < 10) {
			ink[x - 1][y + 1]++;
		}

		if (x + 1 < 10 && 0 <= y - 1) {
			ink[x + 1][y - 1]++;
		}

		if (x + 1 < 10 && y + 1 < 10) {
			ink[x + 1][y + 1]++;
		}
	}

	private static void large(int x, int y) {

		medium(x, y);

		if (x + 2 < 10) {
			ink[x + 2][y]++;
		}

		if (0 <= x - 2) {
			ink[x - 2][y]++;
		}

		if (y + 2 < 10) {
			ink[x][y + 2]++;
		}

		if (0 <= y - 2) {
			ink[x][y - 2]++;
		}
	}
}

