	private static int[][] smallInk(int x, int y, int[][] array) {
		if (x - 1 >= 0) {
			array[y][x - 1]++;
		}

		if (y - 1 >= 0) {
			array[y - 1][x]++;
		}
		array[y][x]++;
		if (y + 1 < n) {
			array[y + 1][x]++;
		}

		if (x + 1 < m) {
			array[y][x + 1]++;
		}
		return array;
	}

	private static int[][] mediumInk(int x, int y, int[][] array) {
		array = smallInk(x, y, array);
		if (y - 1 >= 0) {
			if (x - 1 >= 0)
				array[y - 1][x - 1]++;
			if (x + 1 < m)
				array[y - 1][x + 1]++;
		}
		if (y + 1 < n) {
			if (x - 1 >= 0)
				array[y + 1][x - 1]++;
			if (x + 1 < m)
				array[y + 1][x + 1]++;
		}

		return array;
	}

	private static int[][] largeInk(int x, int y, int[][] array) {
		array = mediumInk(x, y, array);
		if (x - 2 >= 0) {
			array[y][x - 2]++;
		}

		if (y - 2 >= 0) {
			array[y - 2][x]++;
		}
		if (y + 2 < n) {
			array[y + 2][x]++;
		}

		if (x + 2 < m) {
			array[y][x + 2]++;
		}

		return array;
	}

}

