import static java.util.Arrays.*;
import static java.lang.Math.*;

public class Main {
	int INF = 1 << 28;
	double EPS = 1e-10;
	int OFS = 2;

	int[][] map;

	void run() {
		Scanner sc = new Scanner(System.in);
		map = new int[10 + 4][10 + 4];
		for(;sc.hasNext(); ) {
			String[] ss = sc.next().split(",");
			int[] val = new int[3];

			for(int i=0;i<3;i++) val[i] = Integer.parseInt(ss[i]);
			if(val[2] == 1) small(val[0], val[1]);
			if(val[2] == 2) medium(val[0], val[1]);
			if(val[2] == 3) large(val[0], val[1]);
		}

		int sp = 0, max = 0;
		for(int x=2;x<10+2;x++) for(int y=2;y<10+2;y++) {
			if(map[y][x] != 0) max = max(max, map[y][x]);
			else sp++;
		}
		System.out.println(sp);
		System.out.println(max);
	}

	void large(int x, int y) {
		for(int ny=-2;ny<=2;ny++) {
			for(int nx=-( 2-abs(ny) );nx<=2-abs(ny);nx++) 
				map[y+ny+2][x+nx+2]++;
		}
	}

	void medium(int x, int y) {
		for(int i=-1;i<=1;i++) for(int j=-1;j<=1;j++) {
			map[y+j+2][x+i+2]++;
		}
	}

	void small(int x, int y) {
		map[y-1+2][x+2]++; map[y+2][x-1+2]++; map[y+1+2][x+2]++; map[y+2][x+1+2]++;
	}

	public static void main(String[] args) {
		new Main().run();
	}
}

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;


public class Main {
	private	static	BufferedReader	br    = null;
	private	static	int[][]			map   = null;
	private	static	int				blank = 100;
	private	static	int				depth = 0;

	static {
		br  = new BufferedReader(new InputStreamReader(System.in));
		map = new int[10][10];
	}

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[]	aParam = null;

		while((aParam = paramParam()) != null) {
			switch(aParam[2]) {
			case 3:
				paint(aParam[0], aParam[1]-2);
				paint(aParam[0]-2, aParam[1]);
				paint(aParam[0]+2, aParam[1]);
				paint(aParam[0], aParam[1]+2);
			case 2:
				paint(aParam[0]-1, aParam[1]-1);
				paint(aParam[0]+1, aParam[1]-1);
				paint(aParam[0]-1, aParam[1]+1);
				paint(aParam[0]+1, aParam[1]+1);
			case 1:
				paint(aParam[0], aParam[1]-1);
				paint(aParam[0]-1, aParam[1]);
				paint(aParam[0], aParam[1]);
				paint(aParam[0]+1, aParam[1]);
				paint(aParam[0], aParam[1]+1);
				break;
			}
		}

		System.out.println(blank);
		System.out.println(depth);
	}

	private static void paint(int x, int y) {
		if (0 <= x && x < 10) {
			if (0 <= y && y < 10) {
				int state = map[y][x];
				if (state++ == 0) {
					blank--;
				}
				depth = Math.max(state, depth);
				map[y][x] = state;
			}
		}
	}

	private static int[] paramParam() {
		int[]	aParam = null;
		String	strIn = null;

		if ((strIn = parseStdin()) != null) {
			String[]	aN = strIn.split(",");
			aParam = new int[3];
			aParam[0] = Integer.parseInt(aN[0]);
			aParam[1] = Integer.parseInt(aN[1]);
			aParam[2] = Integer.parseInt(aN[2]);
		}

		return aParam;
	}

	private static String parseStdin() {
		String	strNum = null;

		try {
			String line = br.readLine();
			if (line != null) {
				if (!line.isEmpty()) {
					strNum = line;
				}
			}
		}
		catch (IOException e) {}

		return strNum;
	}
}

