import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;


public class Main {

	public static void main(String[] a){

		BufferedReader bf = new BufferedReader(new InputStreamReader(System.in));

		String tmpString = null;
		int[][] ans = new int[10][10];
		int count = 0;
		int max_color = 0;

		try {
			while(null != (tmpString = bf.readLine())){
				String[] tmp = tmpString.split(",");
				ans = poton(ans, Integer.parseInt(tmp[0]), Integer.parseInt(tmp[1]), Integer.parseInt(tmp[2]));
			}
			for(int i = 0; i < 10; i++){
				for(int j = 0; j < 10; j++){
					if(ans[i][j] == 0){
						count++;
					} else if(ans[i][j] > max_color) {
						max_color = ans[i][j];
					}
				}
			}
			System.out.println(count + " " + max_color);
		} catch (IOException e) {
		}
	}

	private static int[][] poton(int[][] ans, int x, int y, int size){
		switch(size){
		case 3:
			if(x + 2 < 10)ans[x + 2][y] -= -1;
			if(x - 2 >= 0)ans[x - 2][y] -= -1;
			if(y + 2 < 10)ans[x][y + 2] -= -1;
			if(y - 2 >= 0)ans[x][y - 2] -= -1;
		case 2:
			if(x + 1 < 10 && y + 1 < 10)ans[x + 1][y + 1] -= -1;
			if(x + 1 < 10 && y - 1 >= 0)ans[x + 1][y - 1] -= -1;
			if(x - 1 >= 0 && y + 1 < 10)ans[x - 1][y + 1] -= -1;
			if(x - 1 >= 0 && y - 1 >= 0)ans[x - 1][y - 1] -= -1;
		case 1:
			ans[x][y] -= -1;
			if(x + 1 < 10)ans[x + 1][y] -= -1;
			if(x - 1 >= 0)ans[x - 1][y] -= -1;
			if(y + 1 < 10)ans[x][y + 1] -= -1;
			if(y - 1 >= 0)ans[x][y - 1] -= -1;
		}
		return ans;
	}
}

import java.util.Scanner;
public class Main {
   public static final int MAPSIZE = 10;
   public static void main(String[] args) {
      Scanner sc = new Scanner(System.in);
      /*we prepare bigger field*/
      int[][] field = new int[MAPSIZE + 4][MAPSIZE + 4];

      while(sc.hasNext()) {
         /*get coordinates and magnitude of the ink*/
         String[] input = sc.nextLine().split(",");      

         /*if null input, break the loop*/
         if(input.length == 1) {
            break;
         }         

         /*parse the input data to integer*/
         int x = Integer.parseInt(input[0]);
         int y = Integer.parseInt(input[1]);
         int size = Integer.parseInt(input[2]);

         /*drop the ink*/
         drop(field, x, y, size);         
      }// end of while loop

      /*only go through valid part of the field and count value with 0*/
      int count = 0;
      int max = 0;
      for(int i = 2; i <= MAPSIZE + 1; i ++) {
         for(int j = 2; j <= MAPSIZE + 1; j ++) {
            if(field[i][j] == 0) {
               count ++;
            } else if(field[i][j] > max) {
               max = field[i][j];
            }
         }
      }

      /*print out the reuslt*/
      System.out.println(count);
      System.out.println(max);
   }// end of main method


   public static void drop(int[][] field, int x, int y, int size) {
      switch(size) {
         case 1:
            dropMin(field, y, x); //because the array is configured differently, x and y are switched.
            break;
         case 2:
            dropMiddle(field, y, x);
            break;
         case 3:
            dropMax(field, y, x);
            break;
      }
   }


   public static void dropMin(int[][] field, int x, int y) {
         field[x + 2][y + 2] ++; //center
         field[x + 2][y + 1] ++; //up
         field[x + 2][y + 3] ++; //down
         field[x + 1][y + 2] ++; //left
         field[x + 3][y + 2] ++; //right
   }

   public static void dropMiddle(int[][] field, int x, int y) {
      dropMin(field, x, y);
      field[x + 3][y + 3] ++;
      field[x + 3][y + 1] ++;
      field[x + 1][y + 1] ++;
      field[x + 1][y + 3] ++;
   }

   public static void dropMax(int[][] field, int x, int y) {
      dropMiddle(field, x, y);
      field[x + 2][y] ++;
      field[x + 2][y + 4] ++;
      field[x][y + 2] ++;
      field[x + 4][y + 2] ++;
   }

}// end of class

import java.util.Arrays;
import java.util.Scanner;

public class Main {
	private int[][] paper = new int[10][10];

	public static void main(String[] args) {
		new Main().run();
	}

	private void run() {
		Scanner scan = new Scanner(System.in);

		for (int i = 0; i < 10; i++) Arrays.fill(paper[i], 0);

		while (scan.hasNext()) {
			String str = scan.nextLine();
			int x = Integer.parseInt(str.substring(0, 1));
			int y = Integer.parseInt(str.substring(2, 3));
			int s = Integer.parseInt(str.substring(4, 5));

			try {
				paper[x][y]++;
				if (s == 3) {
					large(x,y);
				}else if (s == 2) {
					medium(x, y);
				}else {
					small(x, y);
				}
			}catch (ArrayIndexOutOfBoundsException e) {

			}
			int count = 0, max = -1;
			for (int i = 0; i < 10; i++) {
				for (int j = 0; j < 10; j++) {
					if (paper[i][j] == 0) count++;
					max = Math.max(max, paper[i][j]);
				}
			}
			System.out.println(count);
			System.out.println(max);
		}

	}

	private void large(int x, int y) {
		medium(x, y);
		paper[x][y - 2]++;
		paper[x][y + 2]++;
		paper[x - 2][y]++;
		paper[x + 2][y]++;
	}

	private void medium(int x, int y) {
		small(x, y);
		paper[x - 1][y - 1]++;
		paper[x - 1][y + 1]++;
		paper[x + 1][y - 1]++;
		paper[x + 1][y + 1]++;
	}

	private void small(int x, int y) {
		paper[x][y - 1]++;
		paper[x][y + 1]++;
		paper[x - 1][y]++;
		paper[x + 1][y]++;
	}

	private void debug() {
		System.out.println();
	}

}

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

public class Main {

	private static int ink[][] = new int[10][10];
	private static String line[] = new String[3];
	private static int clearCells = 0;
	private static int mostDeepCell = 0;

	public static void main(String[] args) throws NumberFormatException,
			IOException {

		InputStreamReader isr = new InputStreamReader(System.in);
		BufferedReader br = new BufferedReader(isr);

		String str = br.readLine();
		while (str != null || !str.equals("")) {

			line = str.split(",", 3);

			int x = Integer.parseInt(line[0]);
			int y = Integer.parseInt(line[1]);
			int size = Integer.parseInt(line[2]);

			if (size == 1) {
				small(x, y);
			}

			if (size == 2) {
				medium(x, y);
			}

			if (size == 3) {
				large(x, y);
			}
			for (int i = 0; i < 10; ++i) {
				for (int j = 0; j < 10; ++j) {

					if (ink[i][j] == 0) {
						clearCells++;
					}

					if (mostDeepCell < ink[i][j]) {
						mostDeepCell = ink[i][j];
					}
				}
			}

			System.out.println(clearCells);
			System.out.println(mostDeepCell);

		}
	}

	private static void small(int x, int y) {

		ink[x][y]++;

		if (0 <= x - 1) {
			ink[x - 1][y]++;
		}

		if (x + 1 < 10) {
			ink[x + 1][y]++;
		}

		if (0 <= y - 1) {
			ink[x][y - 1]++;
		}

		if (y + 1 < 10) {
			ink[x][y + 1]++;
		}
	}

	private static void medium(int x, int y) {

		small(x, y);

		if (0 <= x - 1 && 0 <= y - 1) {
			ink[x - 1][y - 1]++;
		}

		if (0 <= x - 1 && y + 1 < 10) {
			ink[x - 1][y + 1]++;
		}

		if (x + 1 < 10 && 0 <= y - 1) {
			ink[x + 1][y - 1]++;
		}

		if (x + 1 < 10 && y + 1 < 10) {
			ink[x + 1][y + 1]++;
		}
	}

	private static void large(int x, int y) {

		medium(x, y);

		if (x + 2 < 10) {
			ink[x + 2][y]++;
		}

		if (0 <= x - 2) {
			ink[x - 2][y]++;
		}

		if (y + 2 < 10) {
			ink[x][y + 2]++;
		}

		if (0 <= y - 2) {
			ink[x][y - 2]++;
		}
	}
}

import java.io.BufferedReader;
import java.io.InputStreamReader;

public class Main {
	private final static byte MASK_RIGHT = 0x0F; //4bit
	private final static int STRIP_LENGTH = 10; //1
	private static int[] punchTape = new int[100]; //10*10

	public static void main(String[] args) throws Exception {
		BufferedReader reader = new BufferedReader(new InputStreamReader(System.in), 256);
		String sLine;
		while ((sLine = reader.readLine()) != null) {
			droppingInk(sLine.charAt(0) & MASK_RIGHT, sLine.charAt(2) & MASK_RIGHT, sLine.charAt(4) & MASK_RIGHT);
		}
		calcResult();
	}

	/**
	 *
	 */
	private static void droppingInk(int x, int y, int size) {
		sinking(x, y - 1);
		sinking(x - 1, y);
		sinking(x, y);
		sinking(x + 1, y);
		sinking(x, y + 1);
		if (size == 1) {
			return;
		}

		sinking(x - 1, y - 1);
		sinking(x + 1, y - 1);
		sinking(x - 1, y + 1);
		sinking(x + 1, y + 1);
		if (size == 2) {
			return;
		}

		sinking(x, y - 2);
		sinking(x - 2, y);
		sinking(x + 2, y);
		sinking(x, y + 2);
	}

	/**
	 * 
	 */
	private static void sinking(int x, int y) {
		if (x >= 0 && x < 10 && y >= 0 && y < 10) {
			punchTape[x * STRIP_LENGTH + y]++;
		}
	}

	/**
	 *
	 */
	private static void calcResult() {
		int white = 0;//
		int max = 0;//
		for (int cell : punchTape) {
			if (cell == 0) {
				white++;
			} else if (cell > max) {
				max = cell;
			}
		}
		System.out.println(white);
		System.out.println(max);
	}
}

