import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.StringTokenizer;

public class V00026 {

	private static BufferedReader br;
	private static int[][] squares;

	static {
		br = new BufferedReader(new InputStreamReader(System.in));
		squares = new int[10][10];
	}

	public static void main(String[] args) throws IOException {

		String line;
		int x, y, size, blind = 100, max = 0;

		while ((line = br.readLine()) != null && !line.isEmpty()) {
			StringTokenizer st = new StringTokenizer(line, ",");

			while (st.hasMoreTokens()) {
				x = Integer.parseInt(st.nextToken());
				y = Integer.parseInt(st.nextToken());
				size = Integer.parseInt(st.nextToken());

				squares[x][y]++;
				switch (size) {
				case 1:
					set1(x, y);
					break;
				case 2:
					set1(x, y);
					set2(x, y);
					break;
				case 3:
					set1(x, y);
					set2(x, y);
					set3(x, y);
				default:
					break;
				}
			}
		}
		for (int[] square : squares) {
			for (int value : square) {
				if (value != 0) {
					blind--;
				}
				if (max < value) {
					max = value;
				}
			}
		}
		System.out.println(blind + "\n" + max);
	}

	private static void set1(int x, int y) {
		if (x > 0) {
			squares[x - 1][y]++;
		}
		if (x < 9) {
			squares[x + 1][y]++;
		}
		if (y > 0) {
			squares[x][y - 1]++;
		}
		if (y < 9) {
			squares[x][y + 1]++;
		}
	}

	private static void set2(int x, int y) {
		if (x > 0 && y > 0) {
			squares[x - 1][y - 1]++;
		}
		if (x > 0 && y < 9) {
			squares[x - 1][y + 1]++;
		}
		if (x < 9 && y > 0) {
			squares[x + 1][y - 1]++;
		}
		if (x < 9 && y < 10) {
			squares[x + 1][y + 1]++;
		}
	}

	private static void set3(int x, int y) {
		if (x - 1 > 0) {
			squares[x - 2][y]++;
		}
		if (x + 1 < 9) {
			squares[x + 2][y]++;
		}
		if (y - 1 > 0) {
			squares[x][y - 2]++;
		}
		if (y + 1 < 9) {
			squares[x][y + 2]++;
		}
	}
}

import java.util.Arrays;
import java.util.Scanner;

public class Main {
	private int[][] paper = new int[10][10];

	public static void main(String[] args) {
		new Main().run();
	}

	private void run() {
		Scanner scan = new Scanner(System.in);

		for (int i = 0; i < 10; i++) Arrays.fill(paper[i], 0);

		while (scan.hasNext()) {
			String str = scan.nextLine();
			int x = Integer.parseInt(str.substring(0, 1));
			int y = Integer.parseInt(str.substring(2, 3));
			int s = Integer.parseInt(str.substring(4, 5));

			if(!out(x, y)) paper[x][y]++;
			if (s == 3) {
				large(x,y);
			}else if (s == 2) {
				medium(x, y);
			}else {
				small(x, y);
			}
		}
		int count = 0, max = -1;
		for (int i = 0; i < 10; i++) {
			for (int j = 0; j < 10; j++) {
				if (paper[i][j] == 0) count++;
				max = Math.max(max, paper[i][j]);
			}
		}
		System.out.println(count);
		System.out.println(max);

	}

	private void large(int x, int y) {
		medium(x, y);
		if(!out(x, y - 2)) paper[x][y - 2]++;
		if(!out(x, y + 2)) paper[x][y + 2]++;
		if(!out(x - 2, y)) paper[x - 2][y]++;
		if(!out(x + 2, y)) paper[x + 2][y]++;
	}

	private void medium(int x, int y) {
		small(x, y);
		if(!out(x - 1, y - 1)) paper[x - 1][y - 1]++;
		if(!out(x - 1, y + 1)) paper[x - 1][y + 1]++;
		if(!out(x + 1, y - 1)) paper[x + 1][y - 1]++;
		if(!out(x + 1, y + 1)) paper[x + 1][y + 1]++;
	}

	private void small(int x, int y) {
		if(!out(x, y - 1)) paper[x][y - 1]++;
		if(!out(x, y + 1)) paper[x][y + 1]++;
		if(!out(x - 1, y)) paper[x - 1][y]++;
		if(!out(x + 1, y)) paper[x + 1][y]++;
	}

	private boolean out(int x, int y) {
		if (x >= 0 && y >= 0 && x < 10 && y < 10) {
			return false;
		}else {
			return true;
		}
	}

	private void debug() {
		System.out.println();
	}

}

