	}

	static void makeMiddleArea(int[][] axis, int xAxis, int yAxis) {
		makeSmallArea(axis, xAxis, yAxis);
		axis[xAxis + 1][yAxis + 1]++;
		axis[xAxis + 1][yAxis - 1]++;
		axis[xAxis - 1][yAxis + 1]++;
		axis[xAxis - 1][yAxis - 1]++;

	}

	static void makeBigArea(int[][] axis, int xAxis, int yAxis) {
		makeMiddleArea(axis, xAxis, yAxis);
		axis[xAxis][yAxis + 2]++;
		axis[xAxis][yAxis - 2]++;
		axis[xAxis + 2][yAxis]++;
		axis[xAxis - 2][yAxis]++;

	}

}

