					max = paper[i][j];
				}
			}
		}

		System.out.println(emptySpace);
		System.out.println(max);
	}

	// 
	public static void drop(int paper[][], int x, int y, int size) {
		if (size == 1) {
			drop1(paper, x, y);
		}
		if (size == 2) {
			drop2(paper, x, y);
		}
		if (size == 3) {
			drop3(paper, x, y);
		}
	}

	// 
	public static void drop1(int paper[][], int x, int y) {
		paper[x][y]++;
		if (x + 1 < 10) {
			paper[x + 1][y]++;
		}
		if (x - 1 > -1) {
			paper[x - 1][y]++;
		}
		if (y + 1 < 10) {
			paper[x][y + 1]++;
		}
		if (y - 1 > -1) {
			paper[x][y - 1]++;
		}
	}

	// 
	public static void drop2(int paper[][], int x, int y) {
		drop1(paper, x, y);
		if (x + 1 < 10 && y + 1 < 10) {
			paper[x + 1][y + 1]++;
		}
		if (x + 1 < 10 && y - 1 > -1) {
			paper[x + 1][y - 1]++;
		}
		if (x - 1 > -1 && y + 1 < 10) {
			paper[x - 1][y + 1]++;
		}
		if (x - 1 > -1 && y - 1 > -1) {
			paper[x - 1][y - 1]++;
		}
	}

	// 
	public static void drop3(int paper[][], int x, int y) {
		drop2(paper, x, y);
		if (x + 2 < 10) {
			paper[x + 2][y]++;
		}
		if (x - 2 > -1) {
			paper[x - 2][y]++;
		}
		if (y + 2 < 10) {
			paper[x][y + 2]++;
		}
		if (y - 2 > -1) {
			paper[x][y - 2]++;
		}
	}
}

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.Arrays;

public class Main {

	static final int n = 10;

	static final int m = 10;

	static final String INK_SIZE_SMALL = "1";

	static final String INK_SIZE_MEDIUM = "2";

	static final String INK_SIZE_LARGE = "3";

	public static void main(String[] args) throws IOException {

		int[][] paper = new int[n][m];

		BufferedReader bufferedReader = new BufferedReader(
				new InputStreamReader(System.in), 1);

		String line;
		while ((line = bufferedReader.readLine()) != null) {
			String inkSize = line.split(",")[2];
			int x = Integer.parseInt(line.split(",")[0]);
			int y = Integer.parseInt(line.split(",")[1]);

			if (INK_SIZE_SMALL.equals(inkSize)) {
				paper = smallInk(x, y, paper);
			} else if (INK_SIZE_MEDIUM.equals(inkSize)) {
				paper = mediumInk(x, y, paper);
			} else if (INK_SIZE_LARGE.equals(inkSize)) {
				paper = largeInk(x, y, paper);
			}
		}

		int colorless = 0;
		int depth = 0;

		for (int i = 0; i < n; i++) {
			for (int j = 0; j < m; j++) {
				if (paper[i][j] == 0) {
					colorless++;
				}
				if (depth < paper[i][j]) {
					depth = paper[i][j];
				}
			}
		}
		System.out.println(colorless);
		System.out.println(depth);
	}

	private static int[][] smallInk(int x, int y, int[][] array) {
		if (x - 1 >= 0) {
			array[y][x - 1]++;
		}

		if (y - 1 >= 0) {
			array[y - 1][x]++;
		}
		array[y][x]++;
		if (y + 1 < n) {
			array[y + 1][x]++;
		}

		if (x + 1 < m) {
			array[y][x + 1]++;
		}
		return array;
	}

	private static int[][] mediumInk(int x, int y, int[][] array) {
		array = smallInk(x, y, array);
		if (y - 1 >= 0) {
			if (x - 1 >= 0)
				array[y - 1][x - 1]++;
			if (x + 1 < m)
				array[y - 1][x + 1]++;
		}
		if (y + 1 < n) {
			if (x - 1 >= 0)
				array[y + 1][x - 1]++;
			if (x + 1 < m)
				array[y + 1][x + 1]++;
		}

		return array;
	}

	private static int[][] largeInk(int x, int y, int[][] array) {
		array = mediumInk(x, y, array);
		if (x - 2 >= 0) {
			array[y][x - 2]++;
		}

		if (y - 2 >= 0) {
			array[y - 2][x]++;
		}
		if (y + 2 < n) {
			array[y + 2][x]++;
		}

		if (x + 2 < m) {
			array[y][x + 2]++;
		}

		return array;
	}

}

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

public class Main{
	public static void main(String[] args) throws IOException{
		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
		String str;
		int blocks[][] = new int[10][10];
		int blank=0,max=0;

		while((str=br.readLine()) != null){
			String strArray[] = str.split(",");
			int x = Integer.parseInt(strArray[0]);
			int y = Integer.parseInt(strArray[1]);
			int size = Integer.parseInt(strArray[2]);

			drop(blocks, x, y, size);

		}

		for(int i=0; i<10; i++){
			for(int j=0; j<10; j++){
				if(blocks[i][j]==0){
					blank++;
				}else if(blocks[i][j]>max){
					max = blocks[i][j];
				}
			}
		}

		System.out.println(blank);
		System.out.println(max);

	}

	static void drop(int blocks[][],int x, int y, int size){
		if(size == 1)//
			min_drop(blocks, x, y);
		if(size == 2)
			mid_drop(blocks, x, y);
		if(size == 3)
			lag_drop(blocks, x, y);
	}

	static void min_drop(int blocks[][], int x, int y){
		blocks[x][y]++;
		if(x-1>-1)
			blocks[x-1][y]++;
		if(x+1<10)
			blocks[x+1][y]++;
		if(y-1>-1)
			blocks[x][y-1]++;
		if(y+1<10)
			blocks[x][y+1]++;
	}

	static void mid_drop(int blocks[][], int x, int y){
		min_drop(blocks, x, y);
		if(-1<x-1 && -1<y-1)
			blocks[x-1][y-1]++;
		if(x+1<10 && -1<y-1)
			blocks[x+1][y-1]++;
		if(-1<x-1 && y+1<10)
			blocks[x-1][y+1]++;
		if(x+1<10 && y+1<10)
			blocks[x+1][y+1]++;
	}

	static void lag_drop(int blocks[][], int x, int y){
		mid_drop(blocks, x, y);
		if(-1<y-2)
			blocks[x][y-2]++;
		if(y+2<10)
			blocks[x][y+2]++;
		if(-1<x-2)
			blocks[x-2][y]++;
		if(x+2<10)
			blocks[x+2][y]++;
	}
}

