		while (true) {

			String inputData = input.readLine();
			// null
			if (inputData == null || inputData.equals("")) {
				break;
			}
			// (0XY)
			String[] data = inputData.split(",");
			// 
			if (Integer.parseInt(data[SIZE]) == 1) {
				makeSmallArea(axis, Integer.parseInt(data[X]),
						Integer.parseInt(data[Y]));
			}

			if (Integer.parseInt(data[SIZE]) == 2) {
				makeMiddleArea(axis, Integer.parseInt(data[X]),
						Integer.parseInt(data[Y]));

			}

			if (Integer.parseInt(data[SIZE]) == 3) {
				makeBigArea(axis, Integer.parseInt(data[X]),
						Integer.parseInt(data[Y]));

			}

		}
		// (0)
		countAxis = 0;
		for (int countX = 0; countX < 10; countX++) {
			for (int countY = 0; countY < 10; countY++) {
				if (axis[countX][countY] == 0) {
					countAxis++;
				}
				// 
				if (axis[countX][countY] > maxThickness) {
					maxThickness = axis[countX][countY];
				}

			}
		}
		// 
		System.out.println(countAxis);
		System.out.println(maxThickness);
	}

	// if
	static void makeSmallArea(int[][] axis, int xAxis, int yAxis) {
		if (xAxis >= 1) {
			axis[xAxis - 1][yAxis]++;
		}
		if (xAxis <= 8) {
			axis[xAxis + 1][yAxis]++;
		}
		if (yAxis >= 1) {
			axis[xAxis][yAxis - 1]++;
		}
		if (yAxis <= 8) {
			axis[xAxis][yAxis + 1]++;
		}
		axis[xAxis][yAxis]++;
	}

	// if
	static void makeMiddleArea(int[][] axis, int xAxis, int yAxis) {
		makeSmallArea(axis, xAxis, yAxis);
		if (xAxis >= 1 && yAxis >= 1) {
			axis[xAxis - 1][yAxis - 1]++;

		}
		if (xAxis >= 1 && yAxis <= 8) {
			axis[xAxis - 1][yAxis + 1]++;

		}
		if (xAxis <= 8 && yAxis >= 1) {
			axis[xAxis + 1][yAxis - 1]++;
		}
		if (xAxis <= 8 && yAxis <= 8) {
			axis[xAxis + 1][yAxis + 1]++;

		}
	}

	// if
	static void makeBigArea(int[][] axis, int xAxis, int yAxis) {
		makeMiddleArea(axis, xAxis, yAxis);
		if (xAxis >= 2) {
			axis[xAxis - 2][yAxis]++;
		}
		if (xAxis <= 7) {
			axis[xAxis + 2][yAxis]++;
		}
		if (yAxis >= 2) {
			axis[xAxis][yAxis - 2]++;
		}
		if (yAxis <= 7) {
			axis[xAxis][yAxis + 2]++;
		}
	}
}

import java.util.*;
public class Main {
	static int[][] masu = new int[10][10];
	static void doIt(int x,int y,int size){
		switch(size){
		case 1:
			small(x,y);
			break;
		case 2:
			mid(x,y);
			break;
		case 3:
			big(x,y);
			break;
		}
	}
	static void small(int x,int y){
		int sx[]={1,0,-1,0},sy[]={0,1,0,-1};
		for(int i=0;i<4;i++){
			int nx=sx[i]+x;
			int ny=sy[i]+y;
			draw(nx,ny);
		}
	}
	static void mid(int x,int y){
		for(int s=-1;s<2;s++){
			for(int i=-1;i<2;i++){
				draw(x+i,y+s);
			}
		}
	}
	static void big(int x,int y){
		mid(x,y);
		draw(x,y-2);draw(x,y+2);draw(x+2,y);draw(x-2,y);
	}
	static void draw(int x,int y){
		if(x<0||y<0||x>9||y>9)return;
		masu[y][x]+=1;
	}
	public static void main(String[] args){
		Scanner in = new Scanner(System.in);
		for(int s=0;s<10;s++)for(int i=0;i<10;i++)masu[s][i]=0;
		while(in.hasNext()){
			String[] input = in.next().split(",");
			int x=Integer.parseInt(input[0]),y=Integer.parseInt(input[1]),size=Integer.parseInt(input[2]);
			doIt(x,y,size);
		}
		int big=0, cnt=0;
		for(int s=0;s<10;s++)for(int i=0;i<10;i++){
			big=Math.max(big, masu[s][i]);
			if(masu[s][i]==0)cnt++; 
		}
		System.out.println(cnt);
		System.out.println(big);


	}
}

