import java.util.HashMap;
import java.util.Iterator;
import java.util.Map;
import java.util.Scanner;

public class Main {

	/**
	 * @param args
	 */
	public static void main(String[] args) {

		System.out.println("");
		Scanner sc = new Scanner(System.in);

		Map<String, Integer> dropingMap = new HashMap<String, Integer>();
		for (int x = 0; x < 10; x++) {
			for (int y = 0; y < 10; y++) {
				dropingMap.put(String.valueOf(x).concat(String.valueOf(y)), 0);

			}
		}
		while (sc.hasNext()) {
			String[] s = sc.next().split(",");
			int dx = Integer.parseInt(s[0]);
			int dy = Integer.parseInt(s[1]);
			int d = Integer.parseInt(s[2]);

			if (d == 1) {

				dropMin(dx, dy, dropingMap);

			} else if (d == 2) {

				dropMid(dx, dy, dropingMap);

			} else {
				dropMax(dx, dy, dropingMap);
			}

			checkMax(dropingMap);
		}

	}

	public static void checkMax(Map<String, Integer> dropingMap) {

		int maxVal = 0;
		int coutZero = 0;
		for (Iterator dropMap = dropingMap.entrySet().iterator(); dropMap.hasNext();) {
			Map.Entry entry = (Map.Entry) dropMap.next();
			String key = (String) entry.getKey();
			int value = (Integer) entry.getValue();

			if (maxVal < value) {
				maxVal = value;
			}
			if (value == 0) {
				coutZero = coutZero + 1;
			}
		}
		System.out.println(coutZero);
		System.out.println(maxVal);

	}

	//String1x 2y
	public static void dropMin(int x, int y, Map<String, Integer> dropMapData) {

		for (Iterator dropMap = dropMapData.entrySet().iterator(); dropMap.hasNext();) {
			Map.Entry entry = (Map.Entry) dropMap.next();
			String key = (String) entry.getKey();
			refreshMap(x, y, entry, key);

			//
			refreshMap(x, y + 1, entry, key);
			refreshMap(x, y - 1, entry, key);
			refreshMap(x + 1, y, entry, key);
			refreshMap(x - 1, y, entry, key);
		}
	}

	//String1x 2y
	public static void dropMid(int x, int y, Map<String, Integer> dropMapData) {

		for (Iterator dropMap = dropMapData.entrySet().iterator(); dropMap.hasNext();) {
			Map.Entry entry = (Map.Entry) dropMap.next();
			String key = (String) entry.getKey();
			refreshMap(x, y, entry, key);

			//+
			refreshMap(x, y + 1, entry, key);
			refreshMap(x, y - 1, entry, key);
			refreshMap(x + 1, y, entry, key);
			refreshMap(x - 1, y, entry, key);
			refreshMap(x + 1, y + 1, entry, key);
			refreshMap(x - 1, y - 1, entry, key);
			refreshMap(x + 1, y - 1, entry, key);
			refreshMap(x - 1, y + 1, entry, key);
		}

	}

	//String1x 2y
	public static void dropMax(int x, int y, Map<String, Integer> dropMapData) {

		for (Iterator dropMap = dropMapData.entrySet().iterator(); dropMap.hasNext();) {
			Map.Entry entry = (Map.Entry) dropMap.next();
			String key = (String) entry.getKey();
			refreshMap(x, y, entry, key);

			//+
			refreshMap(x, y + 1, entry, key);
			refreshMap(x, y - 1, entry, key);
			refreshMap(x + 1, y, entry, key);
			refreshMap(x - 1, y, entry, key);

			refreshMap(x, y + 2, entry, key);
			refreshMap(x, y - 2, entry, key);
			refreshMap(x + 2, y, entry, key);
			refreshMap(x - 2, y, entry, key);

			refreshMap(x + 1, y + 1, entry, key);
			refreshMap(x - 1, y - 1, entry, key);
			refreshMap(x + 1, y - 1, entry, key);
			refreshMap(x - 1, y + 1, entry, key);
		}

	}

	public static void refreshMap(int x, int y, Map.Entry entry, String key) {

		if (-1 < x && x < 10 && -1 < y && y < 10 && key.equals((String.valueOf(x)).concat(String.valueOf(y)))) {
			entry.setValue((Integer) entry.getValue() + 1);
		}
	}
}

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.Arrays;

/**
 *
 * @author takumi.oodaira
 */
final class Main {

    public static final int FIELD_SIZE = 10;

    private Main() {
    }

    public static void main(final String[] argv) throws IOException {
        final BufferedReader inputReader =
                new BufferedReader(new InputStreamReader(System.in));
        int[] field = new int[FIELD_SIZE * FIELD_SIZE];
        Arrays.fill(field, 0);
        while (true) {
            final String input = inputReader.readLine();
            if (input == null) {
                break;
            }
            String[] inputs = input.split(",");
            switch (Integer.parseInt(inputs[2])) {
                case 1:
                    field = paintS(field, Integer.parseInt(inputs[0]), Integer.parseInt(inputs[1]));
                    break;
                case 2:
                    field = paintM(field, Integer.parseInt(inputs[0]), Integer.parseInt(inputs[1]));
                    break;
                case 3:
                    field = paintL(field, Integer.parseInt(inputs[0]), Integer.parseInt(inputs[1]));
                    break;
                default:
                    break;
            }
        }
        int whiteCount = 0;
        int colorMax = 0;
        for (int i : field) {
            if (i == 0) {
                whiteCount++;
            } else if (i > colorMax) {
                colorMax = i;
            }
        }
        System.out.println(whiteCount);
        System.out.println(colorMax);
    }

    private static void printField (int[] field) {
        for (int i = 0; i < FIELD_SIZE; i++) {
            for (int j = 0; j < FIELD_SIZE; j++) {
                System.out.print(field[j + i * 10]);
            }
            System.out.println();
        }
    }

    private static int getIndex(final int x, final int y) {
        return x + y * FIELD_SIZE;
    }

    private static int[] paintS(int[] field, final int x, final int y) {
        field[getIndex(x, y)]++;
        if (x - 1 >= 0) {
            field[getIndex(x - 1, y)]++;
        }
        if (y - 1 >= 0) {
            field[getIndex(x, y - 1)]++;
        }
        if (x + 1 < FIELD_SIZE) {
            field[getIndex(x + 1, y)]++;
        }
        if (y + 1 < FIELD_SIZE) {
            field[getIndex(x, y + 1)]++;
        }
        return field;
    }

    private static int[] paintM(int[] field, final int x, final int y) {
        field = paintS(field, x, y);
        if (x - 1 >= 0 && y - 1 >= 0) {
            field[getIndex(x - 1, y - 1)]++;
        }
        if (x - 1 >= 0 && y + 1 < FIELD_SIZE) {
            field[getIndex(x - 1, y + 1)]++;
        }
        if (x + 1 < FIELD_SIZE && y - 1 >= 0) {
            field[getIndex(x + 1, y - 1)]++;
        }
        if (x + 1 < FIELD_SIZE && y + 1 < FIELD_SIZE) {
            field[getIndex(x + 1, y + 1)]++;
        }
        return field;
    }

    private static int[] paintL(int[] field, final int x, final int y) {
        field = paintM(field, x, y);
        if (x - 2 >= 0) {
            field[getIndex(x - 2, y)]++;
        }
        if (y - 2 >= 0) {
            field[getIndex(x, y - 2)]++;
        }
        if (x + 2 < FIELD_SIZE) {
            field[getIndex(x + 2, y)]++;
        }
        if (y + 2 < FIELD_SIZE) {
            field[getIndex(x, y + 2)]++;
        }
        return field;
    }
}

