    /**
     * @param x
     * @param y
     */
    private static void sinkLarge(int x, int y) {
        sinkMiddle(x, y);
        ++cells[y][x - 2];
        ++cells[y][x + 2];
        ++cells[y - 2][x];
        ++cells[y + 2][x];
    }

    /**
     * @param x
     * @param y
     */
    private static void sinkMiddle(int x, int y) {
        sinkSmall(x, y);
        ++cells[y - 1][x - 1];
        ++cells[y - 1][x + 1];
        ++cells[y + 1][x - 1];
        ++cells[y + 1][x + 1];
    }

    /**
     * @param x
     * @param y
     */
    private static void sinkSmall(int x, int y) {
        ++cells[y][x];
        ++cells[y - 1][x];
        ++cells[y + 1][x];
        ++cells[y][x - 1];
        ++cells[y][x + 1];
    }

}

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

class Main {

	public static void main(String[] args) {

		// 
		BufferedReader reader = new BufferedReader(new InputStreamReader(
				System.in));

		int[][] paper = new int[10][10];
		while (true) {
			int[] ink = readPair(reader);
			// 
			if (ink == null) {
				break;
			}
			// 
			drop(ink, paper);
		}

		// 
		int white = 0;
		int max = 0;
		for (int i = 0; i < 10; i++) {
			for (int j = 0; j < 10; j++) {
				if (paper[i][j] == 0) {
					white++;
				}
				if (paper[i][j] > max) {
					max = paper[i][j];
				}
			}
		}
		System.out.println(white);
		System.out.println(max);
	}

	/**
	 * 
	 *
	 * @param ink
	 *            
	 * @param paper
	 *            
	 */
	private static final void drop(int[] ink, int paper[][]) {
		if (ink[2] == 1) {
			// 
			dropSmall(ink, paper);
		} else if (ink[2] == 2) {
			// 
			dropMiddle(ink, paper);
		} else if (ink[2] == 3) {
			dropLarge(ink, paper);
		}

	}

	/**
	 * 
	 *
	 * @param ink
	 *            
	 * @param paper
	 *            
	 */
	private static final void dropSmall(int[] ink, int paper[][]) {
		paper[ink[0]][ink[1]]++;
		if (ink[0] > 0) {
			paper[ink[0] - 1][ink[1]]++;
		}
		if (ink[0] < 9) {
			paper[ink[0] + 1][ink[1]]++;
		}
		if (ink[1] > 0) {
			paper[ink[0]][ink[1] - 1]++;
		}
		if (ink[1] < 9) {
			paper[ink[0]][ink[1] + 1]++;
		}
	}

	/**
	 * 
	 *
	 * @param ink
	 *            
	 * @param paper
	 *            
	 */
	private static final void dropMiddle(int[] ink, int paper[][]) {
		dropSmall(ink, paper);
		if (ink[0] > 0 && ink[1] > 0) {
			paper[ink[0] - 1][ink[1] - 1]++;
		}
		if (ink[0] < 9 && ink[1] > 0) {
			paper[ink[0] + 1][ink[1] - 1]++;
		}
		if (ink[0] < 9 && ink[1] < 9) {
			paper[ink[0] + 1][ink[1] + 1]++;
		}
		if (ink[0] > 0 && ink[1] < 9) {
			paper[ink[0] - 1][ink[1] + 1]++;
		}
	}

	/**
	 * 
	 *
	 * @param ink
	 *            
	 * @param paper
	 *            
	 */
	private static final void dropLarge(int[] ink, int paper[][]) {
		dropMiddle(ink, paper);
		if (ink[0] > 1) {
			paper[ink[0] - 2][ink[1]]++;
		}
		if (ink[0] < 8) {
			paper[ink[0] + 2][ink[1]]++;
		}
		if (ink[1] > 1) {
			paper[ink[0]][ink[1] - 2]++;
		}
		if (ink[1] < 8) {
			paper[ink[0]][ink[1] + 2]++;
		}
	}

	/**
	 * 131
	 *
	 * @param reader
	 *            
	 * @return 31
	 */
	private static int[] readPair(BufferedReader reader) {
		int[] result = new int[4];
		try {
			String line = reader.readLine();
			if (line == null || line.isEmpty()) {
				return null;
			}
			String[] array = line.split(",");
			result[0] = Integer.parseInt(array[0]);
			result[1] = Integer.parseInt(array[1]);
			result[2] = Integer.parseInt(array[2]);
		} catch (IOException e) {
			throw new RuntimeException();
		}
		return result;
	}

}

import java.awt.Point;
import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.HashSet;

public class Main {
    // 
    static final int MATH_LENGTH = 10;

    // )
    static int x, y, scale;
    // 1010
    static int[][] math;
    // 
    static HashSet<Point> scopes;

    static void init() {
        x = 0;
        y = 0;
        scale = 0;
        math = new int[MATH_LENGTH][MATH_LENGTH];
        scopes = new HashSet<Point>();
    }

    public static void main(String[] args) {
        // 
        init();

        // 
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        while (true) {
            String inputStr = null;
            try {
                inputStr = br.readLine();
            } catch (IOException e) {
                e.printStackTrace();
            }
            if (inputStr == null) {
                break;
            }

            // ","
            String[] inputSplitStr = inputStr.split(",");
            x = Integer.valueOf(inputSplitStr[0]);
            y = Integer.valueOf(inputSplitStr[1]);
            scale = Integer.valueOf(inputSplitStr[2]);

            // 
            scopes.clear();
            switch (scale) {
            case 1: // 
                setSmallScopes(x, y);
                break;
            case 2: // 
                setMediumScopes(x, y);
                break;
            case 3: // 
                setLargeScopes(x, y);
                break;
            }

            // (1010)1
            for (Point scope : scopes) {
                if (0 <= scope.x && scope.x < MATH_LENGTH && 0 <= scope.y && scope.y < MATH_LENGTH) {
                    math[scope.x][scope.y]++;
                }
            }
        }

        // 
        int whiteNumber = 0;
        int mostNumber = 0;
        for (int i = 0; i < MATH_LENGTH; i++) {
            for (int j = 0; j < MATH_LENGTH; j++) {
                if (math[i][j] == 0) {
                    whiteNumber++;
                }
                if (mostNumber < math[i][j]) {
                    mostNumber = math[i][j];
                }
            }
        }
        System.out.println(whiteNumber);
        System.out.println(mostNumber);
    }

    private static void setSmallScopes(int x, int y) {
        scopes.add(new Point(x - 1, y));
        scopes.add(new Point(x, y - 1));
        scopes.add(new Point(x, y));
        scopes.add(new Point(x, y + 1));
        scopes.add(new Point(x + 1, y));
    }

    private static void setMediumScopes(int x, int y) {
        scopes.add(new Point(x - 1, y - 1));
        scopes.add(new Point(x - 1, y));
        scopes.add(new Point(x - 1, y + 1));

        scopes.add(new Point(x, y - 1));
        scopes.add(new Point(x, y));
        scopes.add(new Point(x, y + 1));

        scopes.add(new Point(x + 1, y - 1));
        scopes.add(new Point(x + 1, y));
        scopes.add(new Point(x + 1, y + 1));
    }

    private static void setLargeScopes(int x, int y) {
        scopes.add(new Point(x - 2, y));

        scopes.add(new Point(x - 1, y - 1));
        scopes.add(new Point(x - 1, y));
        scopes.add(new Point(x - 1, y + 1));

        scopes.add(new Point(x, y - 2));
        scopes.add(new Point(x, y - 1));
        scopes.add(new Point(x, y));
        scopes.add(new Point(x, y + 1));
        scopes.add(new Point(x, y + 2));

        scopes.add(new Point(x + 1, y - 1));
        scopes.add(new Point(x + 1, y));
        scopes.add(new Point(x + 1, y + 1));

        scopes.add(new Point(x + 2, y));
    }
}

