		    p[y-1][x+1]++;
		    p[y+1][x-1]++;
		    p[y][x-2]++;
		    p[y][x+2]++;
		    p[y-2][x]++;
		    p[y+2][x]++;
		}
	    }
	    int max = 0, count = 0;
	    for(int i=2; i<12; i++)
		for(int j=2; j<12; j++){
		    max = Math.max(max, p[j][i]);
		    if(p[j][i]==0)
			count++;
		}
	    System.out.printf("%d\n%d\n",count,max);
	}catch (Exception e){
	    System.out.println("Error");
	}
    }
}

