        // 
        InputStreamReader streamReader = new InputStreamReader(System.in);
        BufferedReader bufferedReader = new BufferedReader(streamReader);

        String line = null;

        while ((line = bufferedReader.readLine()) != null) {
            String[] nums = line.split(",");

