                sinkMiddle(x, y);

            } else if (size == 3) {
                sinkLarge(x, y);

            }
        }

        // 
        for (int i = 2; i < 12; ++i) {
            for (int j = 2; j < 12; ++j) {
                if (cells[i][j] == 0) {
                    ++count;
