import java.util.Arrays;
import java.util.Scanner;


public class Main{

	public static void main(String[] args){
		Ink l = Ink.LARGE;
		Ink m = Ink.MIDDLE;
		Ink s = Ink.SMALL;
		Paper p = new Paper(10);

		Scanner sc = new Scanner(System.in);
		while(sc.hasNextLine()) {
			Scanner scstr = new Scanner(sc.nextLine());
			scstr.useDelimiter(",");

			int x = scstr.nextInt();
			int y = scstr.nextInt();
			Ink i = null;
			int ink_size = scstr.nextInt();
			if(ink_size == 1) i = s;
			else if(ink_size == 2) i = m;
			else if(ink_size == 3) i = l;

			p.drop(x, y, i);
			scstr.close();
		}
		System.out.println(p.countWhiteCells());
		System.out.println(p.maxDensity());
		sc.close();
	}

}

class Paper{
	int[][] paper;

	Paper(int size){
		this.paper = new int[size][size];
		for(int[] row: this.paper) {
			Arrays.fill(row, 0);
		}
	}

	void drop(int x, int y, Ink ink) {
		for(int i = 0; i < ink.range.length; i++) {
			for(int j = 0; j < ink.range.length; j++) {
				if(ink.range[i][j]) paint(this.paper, x - ink.center_x + i, y - ink.center_y + j);
			}
		}
	}

	void paint(int[][] paper, int x, int y) {
		if(0 <= x && x < paper.length && 0 <= y && y < paper.length) paper[x][y]++;
	}

	int countWhiteCells() {
		int result = 0;
		for(int[] i: paper) {
			for(int j: i) {
				if(j == 0) result++;
			}
		}
		return result;
	}

	int maxDensity() {
		int max = 0;
		for(int[] i: paper) {
			for(int j: i) {
				if(j > max) max = j;
			}
		}
		return max;
	}

}

enum Ink{
	SMALL(new boolean[][] {
			{false,	true,	false},
			{true,	true,	true},
			{false,	true,	false}
	}, 1, 1), 

	MIDDLE(new boolean[][] {
			{true,	true,	true},
			{true,	true,	true},
			{true,	true,	true}
	}, 1, 1), 

	LARGE(new boolean[][] {
			{false,	false,	true,	false,	false},
			{false,	true,	true,	true,	false},
			{true,	true,	true,	true,	true},
			{false,	true,	true,	true,	false},
			{false,	false,	true,	false,	false}
	}, 2, 2);

	boolean[][] range;
	int center_x;
	int center_y;

	private Ink(boolean[][] range, int center_x, int center_y) {
		this.range = range;
		this.center_x = center_x;
		this.center_y = center_y;
	}
}

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

public class Main {
	private static int[][] paper = new int[10][10];

	public static void main(String[] args) throws NumberFormatException, IOException {
		BufferedReader reader = new BufferedReader(new InputStreamReader(System.in), 128);
		String sLine;
		while ((sLine = reader.readLine()) != null) {
			droppingInk(sLine.charAt(0) & 0x0F, sLine.charAt(2) & 0x0F, sLine.charAt(4) & 0x0F);
		}
		calcResult();
	}

	private static void droppingInk(int x, int y, int size) {
		staining(x, y - 1);
		staining(x - 1, y);
		staining(x, y);
		staining(x + 1, y);
		staining(x, y + 1);
		if (size == 1) {
			return;
		}

		staining(x - 1, y - 1);
		staining(x + 1, y - 1);
		staining(x - 1, y + 1);
		staining(x + 1, y + 1);
		if (size == 2) {
			return;
		}

		staining(x, y - 2);
		staining(x - 2, y);
		staining(x + 2, y);
		staining(x, y + 2);
	}

	private static void staining(int x, int y) {
		if (x >= 0 && x < 10 && y >= 0 && y < 10) {
			paper[x][y]++;
		}
	}

	private static void calcResult() {
		int white = 0;
		int maxnoud = 0;
		for (int[] row : paper) {
			for (int colum : row) {
				if (colum == 0) {
					white++;
				} else if (colum > maxnoud) {
					maxnoud = colum;
				}
			}
		}
		System.out.println(white);
		System.out.println(maxnoud);
	}
}

import java.util.*;

public class Main {

    public static int[][] map;
    public static int[][] move = {{0,1}, {1,0}, {-1,0}, {0,-1}};
    public static int[][] move2 = {{1,1}, {1,-1}, {-1,1}, {-1,-1}};
    public static boolean[][] used;
    public static int dx;
    public static int dy;
    public static int s;

    public static void drop(int x, int y, int z) {
        if(!(0<=x && x<10 && 0<=y && y<10)) return;
        if(Math.abs(dx-x) + Math.abs(dy-y) > z) return;
        if(used[x][y]) return;

        map[x][y]++;
        used[x][y] = true;
        for(int k=0; k<4; k++) {
            drop(x + move[k][0], y + move[k][1], z);
        }
    }

    public static void main(String[] args) {
        try(Scanner sc = new Scanner(System.in)) {
            map = new int[10][10];
            while(sc.hasNext()) {
                String[] str = sc.next().split(",");
                dx = Integer.parseInt(str[0]);
                dy = Integer.parseInt(str[1]);
                s = Integer.parseInt(str[2]);
                used = new boolean[10][10];
                drop(dx, dy, (s == 3 ? 2:1));
                if(s == 2) {
                    for(int k=0; k<4; k++) {
                        int x = dx + move2[k][0];
                        int y = dy + move2[k][1];
                        if(0<=x && x<10 && 0<=y && y<10) map[x][y]++;
                    }
                }
            }
            int cnt = 0;
            int max = 0;
            for(int x=0; x<10; x++) {
                for(int y=0; y<10; y++) {
                    if(map[x][y] == 0) cnt++;
                    max = Math.max(max, map[x][y]);
                }
            }
            System.out.println(cnt);
            System.out.println(max);
        }
    }
}

