import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

public class Main {
	private static final byte MASK_RIGHT = 0x0F;
	private static int[][] paper = new int[10][10];

	public static void main(String[] args) throws NumberFormatException, IOException {
		BufferedReader reader = new BufferedReader(new InputStreamReader(System.in), 128);
		String sLine;
		while ((sLine = reader.readLine()) != null) {
			droppingInk(sLine.charAt(0) & MASK_RIGHT, sLine.charAt(2) & MASK_RIGHT, sLine.charAt(4) & MASK_RIGHT);
		}
		calcResult();
	}

	private static void droppingInk(int x, int y, int size) {
		staining(x, y - 1);
		staining(x - 1, y);
		staining(x, y);
		staining(x + 1, y);
		staining(x, y + 1);
		if (size == 1) {
			return;
		}

		staining(x - 1, y - 1);
		staining(x + 1, y - 1);
		staining(x - 1, y + 1);
		staining(x + 1, y + 1);
		if (size == 2) {
			return;
		}

		staining(x, y - 2);
		staining(x - 2, y);
		staining(x + 2, y);
		staining(x, y + 2);
	}

	private static void staining(int x, int y) {
		if (x >= 0 && x < 10 && y >= 0 && y < 10) {
			paper[x][y]++;
		}
	}

	private static void calcResult() {
		int white = 0;
		int maxnoud = 0;
		for (int[] row : paper) {
			for (int colum : row) {
				if (colum == 0) {
					white++;
				} else if (colum > maxnoud) {
					maxnoud = colum;
				}
			}
		}
		System.out.println(white);
		System.out.println(maxnoud);
	}
}

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.Arrays;

public class Main {

    //
    static final int n = 10;

    //
    static final int m = 10;

    //
    static final String INK_SIZE_SMALL = "1";

    // 
    static final String INK_SIZE_MEDIUM = "2";

    //
    static final String INK_SIZE_LARGE = "3";

    public static void main(String[] args) throws IOException {

        //
        int[][] paper = new int[n][m];

        // x, y, 
        BufferedReader bufferedReader = new BufferedReader(
                new InputStreamReader(System.in), 1);

        String line;
        while ((line = bufferedReader.readLine()) != null) {
            //
            String inkSize = line.split(",")[2];

            //x
            int x = Integer.parseInt(line.split(",")[0]);

            //y
            int y = Integer.parseInt(line.split(",")[1]);

            //
            if (INK_SIZE_SMALL.equals(inkSize)) {
                paper = smallInk(x, y, paper);
            } else if (INK_SIZE_MEDIUM.equals(inkSize)) {
                paper = mediumInk(x, y, paper);
            } else if (INK_SIZE_LARGE.equals(inkSize)) {
                paper = largeInk(x, y, paper);
            }
        }

        //
        int colorless = 0;

        //
        int depth = 0;

        //
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                if (paper[i][j] == 0) {
                    colorless++;
                }
                if (depth < paper[i][j]) {
                    depth = paper[i][j];
                }
            }
        }
        //
        System.out.println(colorless);
        System.out.println(depth);
    }

    //
    private static int[][] smallInk(int x, int y, int[][] array) {
        if (x - 1 >= 0) {
            array[y][x - 1]++;
        }

        if (y - 1 >= 0) {
            array[y - 1][x]++;
        }
        array[y][x]++;
        if (y + 1 < n) {
            array[y + 1][x]++;
        }

        if (x + 1 < m) {
            array[y][x + 1]++;
        }
        return array;
    }

    //
    private static int[][] mediumInk(int x, int y, int[][] array) {
        //
        array = smallInk(x, y, array);

        //
        if (y - 1 >= 0) {
            if (x - 1 >= 0)
                array[y - 1][x - 1]++;
            if (x + 1 < m)
                array[y - 1][x + 1]++;
        }
        if (y + 1 < n) {
            if (x - 1 >= 0)
                array[y + 1][x - 1]++;
            if (x + 1 < m)
                array[y + 1][x + 1]++;
        }

        return array;
    }

    //
    private static int[][] largeInk(int x, int y, int[][] array) {
        //
        array = mediumInk(x, y, array);

        //
        if (x - 2 >= 0) {
            array[y][x - 2]++;
        }

        if (y - 2 >= 0) {
            array[y - 2][x]++;
        }
        if (y + 2 < n) {
            array[y + 2][x]++;
        }

        if (x + 2 < m) {
            array[y][x + 2]++;
        }

        return array;
    }

}

