import java.util.Scanner;
public class Main {
   public static final int MAPSIZE = 10;
   public static void main(String[] args) {
      Scanner sc = new Scanner(System.in);
      /*we prepare bigger field*/
      int[][] field = new int[MAPSIZE + 4][MAPSIZE + 4];

      while(true) {
         /*get coordinates and magnitude of the ink*/
         String[] input = sc.nextLine().split(",");      

         /*if null input, break the loop*/
         if(input.length == 1) {
            break;
         }         

         /*parse the input data to integer*/
         int x = Integer.parseInt(input[0]);
         int y = Integer.parseInt(input[1]);
         int size = Integer.parseInt(input[2]);

         /*drop the ink*/
         drop(field, x, y, size);         
      }// end of while loop

      /*only go through valid part of the field and count value with 0*/
      int count = 0;
      int max = 0;
      for(int i = 2; i <= MAPSIZE + 1; i ++) {
         for(int j = 2; j <= MAPSIZE + 1; j ++) {
            if(field[i][j] == 0) {
               count ++;
            } else if(field[i][j] > max) {
               max = field[i][j];
            }
         }
      }

      /*print out the reuslt*/
      System.out.println(count);
      System.out.println(max);
   }// end of main method


   public static void drop(int[][] field, int x, int y, int size) {
      switch(size) {
         case 1:
            dropMin(field, y, x); //because the array is configured differently, x and y are switched.
            break;
         case 2:
            dropMiddle(field, y, x);
            break;
         case 3:
            dropMax(field, y, x);
            break;
      }
   }


   public static void dropMin(int[][] field, int x, int y) {
         field[x + 2][y + 2] ++; //center
         field[x + 2][y + 1] ++; //up
         field[x + 2][y + 3] ++; //down
         field[x + 1][y + 2] ++; //left
         field[x + 3][y + 2] ++; //right
   }

   public static void dropMiddle(int[][] field, int x, int y) {
      dropMin(field, x, y);
      field[x + 3][y + 3] ++;
      field[x + 3][y + 1] ++;
      field[x + 1][y + 1] ++;
      field[x + 1][y + 3] ++;
   }

   public static void dropMax(int[][] field, int x, int y) {
      dropMiddle(field, x, y);
      field[x + 2][y] ++;
      field[x + 2][y + 4] ++;
      field[x][y + 2] ++;
      field[x + 4][y + 2] ++;
   }

}// end of class

import java.util.*;
public class Main {
	public static void main(String[] args) {
		Scanner stdIn = new Scanner(System.in);
		int[][] p = new int[10][10]; //x,y
		while(stdIn.hasNext()) {
			String tmp = stdIn.nextLine();
			String[] k = tmp.split(",");
			int x = Integer.parseInt(k[0]);
			int y = Integer.parseInt(k[1]);
			int size = Integer.parseInt(k[2]);

			if(size >= 1) {
				if(check(x,y)) {
					p[x][y]++;
				}
				if(check(x+1,y)) {
					p[x+1][y]++;
				}
				if(check(x-1,y)) {
					p[x-1][y]++;
				}
				if(check(x,y+1)) {
					p[x][y+1]++;
				}
				if(check(x,y-1)) {
					p[x][y-1]++;
				}
			}
			if(size >= 2) {
				if(check(x+1,y+1)) {
					p[x+1][y+1]++;
				}
				if(check(x-1,y-1)) {
					p[x-1][y-1]++;
				}
				if(check(x+1,y-1)) {
					p[x+1][y-1]++;
				}
				if(check(x-1,y+1)) {
					p[x-1][y+1]++;
				}
			}
			if(size >= 3) {
				if(check(x+2,y)) {
					p[x+2][y]++;
				}
				if(check(x-2,y)) {
					p[x-2][y]++;
				}
				if(check(x,y+2)) {
					p[x][y+2]++;
				}
				if(check(x,y-2)) {
					p[x][y-2]++;
				}
			}
		}
		int whiteCounter = 0;
		int max = 0;
		for(int i = 0; i < 10; i++) {
			for(int j = 0; j < 10; j++) {
				if(p[i][j] > max) {
					max = p[i][j];
				}
				if(p[i][j] == 0) {
					whiteCounter++;
				}
			}
		}
		System.out.println(whiteCounter+"\n"+max);
	}
	static boolean check(int x, int y) {
		if(x < 0 || x > 9 || y < 0 || y > 9) {
			return false;
		}
		return true;
	}
}

import java.util.*;
import java.math.*;
import java.text.*;
public class Main {
	public static void main(String[] args){
		Scanner in = new Scanner(System.in);
		int[][] paper = new int[10][10];
		for(int i=0; i<10; i++){
			for(int j=0; j<10; j++){
				paper[i][j] = 0;
			}
		}
		while(in.hasNext()){
			String str = in.next();
			if(str.equals("0,0,0")) break;
			String st[] = str.split(",");
			int x = Integer.parseInt(st[0]);
			int y = Integer.parseInt(st[1]);
			int size = Integer.parseInt(st[2]);
			switch(size){
			case 1:
				paper = small(x,y,paper);
				break;
			case 2:
				paper = middle(x,y,paper);
				break;
			case 3:
				paper = big(x,y,paper);
				break;

			}
		}
		int blank = 0;
		int max = 0;
		for(int i=0; i<10; i++){
			for(int j=0; j<10; j++){
				if(paper[i][j] == 0) blank++;
				else if(max<paper[i][j]) max = paper[i][j];
			}
		}
		System.out.println(blank);
		System.out.println(max);
	}

	public static int[][] small(int x, int y, int[][] paper){
		paper[x][y]++;
		if(x!=0) paper[x-1][y]++;
		if(x!=9) paper[x+1][y]++;
		if(y!=0) paper[x][y-1]++;
		if(y!=9) paper[x][y+1]++;
		return paper;
	}

	public static int[][] middle(int x, int y, int[][] paper){
		for(int i=-1; i<=1; i++){
			if(0<=i+x && i+x<10){
				for(int j=-1; j<=1; j++){
					if(0<=j+y && j+y<10){
						paper[i+x][j+y]++;
					}
				}
			}
		}
		return paper;
	}

	public static int[][] big(int x, int y, int[][] paper){
		for(int i=-1; i<=1; i++){
			if(0<=i+x && i+x<10){
				for(int j=-1; j<=1; j++){
					if(0<=j+y && j+y<10){
						paper[i+x][j+y]++;
					}
				}
			}
		}
		if(x>1) paper[x-2][y]++;
		if(x<8) paper[x+2][y]++;
		if(y>1) paper[x][y-2]++;
		if(y<8) paper[x][y+2]++;
		return paper;
	}
}

