	    y = Integer.parseInt(input[1]);
	    size = input[2];
	    dropInk(x, y);
	    smallInk(x, y);
	    if (size.equals("1")) {
		continue;
	    }
	    mediumInk(x, y);
	    if (size.equals("2")) {
		continue;
	    }
	    largeInk(x, y);
	}
	int max = 0, cnt = 0;
	for (int i = 0; i < 10; i++) {
	    for (int j = 0; j < 10; j++) {
		if (paper[i][j] == 0) {
		    cnt++;
		}
		if (paper[i][j] > max) {
		    max = paper[i][j];
		}
	    }
	}
	System.out.println(cnt);
	System.out.println(max);
