    public static void dropInk (int x, int y) {
	if ((0 <= x && x < 10) && (0 <= y && y < 10)) {
	    paper[x][y]++;
	}
    }
    public static void smallInk (int x, int y) {
	dropInk(x+1, y);
	dropInk(x-1, y);
	dropInk(x, y+1);
	dropInk(x, y-1);	
    }
    public static void mediumInk (int x, int y) {
	dropInk(x+1, y+1);
	dropInk(x-1, y+1);
	dropInk(x+1, y-1);
	dropInk(x-1, y-1);
    }
    public static void largeInk (int x, int y) {
	dropInk(x+2, y);
	dropInk(x-2, y);
	dropInk(x, y+2);
	dropInk(x, y-2);
    }
}

