import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

class Main {
	static int[][] resultList = new int[10][10];
	public static void main(String[] args) throws IOException {
		BufferedReader in = new BufferedReader(new InputStreamReader(System.in));
		//
		String str = in.readLine();
		//
		while (str != null) {
			if("".equals(str)) break;
			String[] strList =str.split(",");
			int row = Integer.parseInt(strList[0]);
			int colum = Integer.parseInt(strList[1]);
			int size =  Integer.parseInt(strList[2]);
			str = in.readLine();
			switch(size){
			case  1:
				smallDrop(row,colum);
				break;
			case  2:
				middleDrop(row,colum);
				break;
			case  3:
				largeDrop(row,colum);
				break;
			}
		}

		//
		reult(resultList);
	}
	//
	static void smallDrop(int row,int colum){
		resultList[row][colum] =+ 1;
		if(colum < 9) resultList[row][colum + 1]++;
		if(row < 9) resultList[row + 1][colum]++;
		if(colum > 0) resultList[row][colum - 1]++;
		if(row > 0)resultList[row -1][colum]++;
	}

	//
	static void middleDrop(int row,int colum){
		smallDrop(row,colum);
		if(row < 9 && colum < 9) resultList[row + 1][colum + 1]++;
		if(row < 9 && colum > 0) resultList[row + 1][colum - 1]++;
		if(row > 0 && colum > 0) resultList[row - 1][colum - 1]++;
		if(row > 0 && colum < 9) resultList[row - 1][colum + 1]++;
	}

	//
	static void largeDrop(int row,int colum){
		middleDrop(row,colum);
		if(colum < 8) resultList[row][colum + 2]++;
		if(row < 8) resultList[row + 2][colum]++;
		if(colum > 1) resultList[row][colum - 2]++;
		if(row > 1)resultList[row -2][colum]++;
	}

	//
	//
	//
	static void reult(int[][] resultList){
		//
		int  cleanSquares = 0;
		//
		int depth = 0;

		//
		for(int i =0 ; resultList.length > i ;  i++){
			for(int j = 0 ; resultList[i].length > j ; j++){
				if(depth < resultList[i][j]) depth = resultList[i][j];
				else if(resultList[i][j] == 0) cleanSquares++;
			}
		}

		//
		System.out.println(cleanSquares);
		System.out.println(depth);
	}

}

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;


public class Main {

	public static void main(String[] a){

		BufferedReader bf = new BufferedReader(new InputStreamReader(System.in));

		String tmpString = null;
		int[][] ans = new int[10][10];
		int count = 0;
		int max_color = 0;

		try {
			while(null != (tmpString = bf.readLine())){
				String[] tmp = tmpString.split(",");
				ans = poton(ans, Integer.parseInt(tmp[0]), Integer.parseInt(tmp[1]), Integer.parseInt(tmp[2]));
			}
			for(int i = 0; i < 10; i++){
				for(int j = 0; j < 10; j++){
					if(ans[i][j] == 0){
						count++;
					} else if(ans[i][j] > max_color) {
						max_color = ans[i][j];
					}
				}
			}
			System.out.println(count);
			System.out.println(max_color);
		} catch (IOException e) {
		}
	}

	private static int[][] poton(int[][] ans, int x, int y, int size){
		switch(size){
		case 3:
			if(x + 2 < 10)ans[x + 2][y] -= -1;
			if(x - 2 >= 0)ans[x - 2][y] -= -1;
			if(y + 2 < 10)ans[x][y + 2] -= -1;
			if(y - 2 >= 0)ans[x][y - 2] -= -1;
		case 2:
			if(x + 1 < 10 && y + 1 < 10)ans[x + 1][y + 1] -= -1;
			if(x + 1 < 10 && y - 1 >= 0)ans[x + 1][y - 1] -= -1;
			if(x - 1 >= 0 && y + 1 < 10)ans[x - 1][y + 1] -= -1;
			if(x - 1 >= 0 && y - 1 >= 0)ans[x - 1][y - 1] -= -1;
		case 1:
			ans[x][y] -= -1;
			if(x + 1 < 10)ans[x + 1][y] -= -1;
			if(x - 1 >= 0)ans[x - 1][y] -= -1;
			if(y + 1 < 10)ans[x][y + 1] -= -1;
			if(y - 1 >= 0)ans[x][y - 1] -= -1;
		}
		return ans;
	}
}

