import java.util.*;

public class Main {
    static Scanner sc = new Scanner(System.in);
    static int[][] map =new int[10][10];
    public static void main(String[] args){
        for(int i=0;i<10;i++){
            for(int j=0;j<10;j++){
                map[i][j]=0;
            }
        }
        while(sc.hasNextLine()){
            String[] s=sc.nextLine().split(",");
            int x=Integer.parseInt(s[0]);
            int y=Integer.parseInt(s[1]);
            int size=Integer.parseInt(s[2]);
            if(size==1){
                small(x,y);
            }else if(size==2){
                medium(x,y);
            }else if(size==3){
                large(x,y);
            }
        }
        int ans1=0,ans2=0;
        for(int i=0;i<10;i++){
            for(int j=0;j<10;j++){
                if(map[i][j]==0){
                    ans1++;
                }
                if(map[i][j]>ans2){
                    ans2=map[i][j];
                }
            }
        }
        System.out.println(ans1);
        System.out.println(ans2);
    }
    static void small(int x,int y){
        for(int i=-1;i<=1;i++){
            for(int j=-1;j<=1;j++){
                if((i+j>=-1)
                        &&(i+j<=1)
                        &&(i-j>=-1)
                        &&(i-j<=1)
                        &&(x+i>=0)
                        &&(x+i<10)
                        &&(y+j>=0)
                        &&(y+j<10)){
                    map[x+i][y+j]++;
                }
            }
        }
    }
    static void medium(int x,int y){
        for(int i=-1;i<=1;i++){
            for(int j=-1;j<=1;j++){
                if((x+i>=0)
                        &&(x+i<10)
                        &&(y+j>=0)
                        &&(y+j<10)){
                    map[x+i][y+j]++;
                }
            }
        }
    }

    static void large(int x,int y){
        for(int i=-2;i<=2;i++){
            for(int j=-2;j<=2;j++){
                if((i+j>=-2)
                        &&(i+j<=2)
                        &&(i-j>=-2)
                        &&(i-j<=2)
                        &&(x+i>=0)
                        &&(x+i<10)
                        &&(y+j>=0)
                        &&(y+j<10)){
                    map[x+i][y+j]++;
                }
            }
        }
    }
}

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

public class Main {
	private static final byte MASK_RIGHT = 0x0F;
	private static int[][] paper = new int[10][10];

	public static void main(String[] args) throws NumberFormatException, IOException {
		BufferedReader reader = new BufferedReader(new InputStreamReader(System.in), 128);
		String sLine;
		while ((sLine = reader.readLine()) != null) {
			droppingInk(sLine.charAt(0) & MASK_RIGHT, sLine.charAt(2) & MASK_RIGHT, sLine.charAt(4) & MASK_RIGHT);
		}
		calcResult();
	}

	private static void droppingInk(int x, int y, int size) {
		staining(x, y - 1);
		staining(x - 1, y);
		staining(x, y);
		staining(x + 1, y);
		staining(x, y + 1);
		if (size == 1) {
			return;
		}

		staining(x - 1, y - 1);
		staining(x + 1, y - 1);
		staining(x - 1, y + 1);
		staining(x + 1, y + 1);
		if (size == 2) {
			return;
		}

		staining(x, y - 2);
		staining(x - 2, y);
		staining(x + 2, y);
		staining(x, y + 2);
	}

	private static void staining(int x, int y) {
		if (x >= 0 && x < 10 && y >= 0 && y < 10) {
			paper[x][y]++;
		}
	}

	private static void calcResult() {
		int white = 0;
		int maxnoud = 0;
		for (int[] row : paper) {
			for (int colum : row) {
				if (colum == 0) {
					white++;
				} else if (colum > maxnoud) {
					maxnoud = colum;
				}
			}
		}
		System.out.println(white);
		System.out.println(maxnoud);
	}
}

