			for(int i = 0; i < map.length;i++){
				for(int j = 0; j < map[i].length;j++){
					if(map[i][j] == 0)num++;
					max = Math.max(max,map[i][j]);
				}
			}

		}finally{
			br.close();
		}
		System.out.printf("%d\n%d\n",num,max);
	}
		static boolean check(int x , int y){
			if(x > 9 || x < 0 || y > 9 || y < 0)return false;
			return true;
		}
}

import java.io.BufferedReader;
import java.io.InputStreamReader;

public class Main {

	public static void main(String[] args) throws Exception {
		final int X = 0;
		final int Y = 1;
		final int SIZE = 2;
		int countAxis = 0;
		int maxThickness = 0;

		// ????????(????????0????????)?????????????
		int[][] axis = new int[10][10];
		for (int countX = 0; countX < 10; countX++) {
			for (int countY = 0; countY < 10; countY++) {
				axis[countX][countY] = 0;
			}
		}
		while (true) {
			BufferedReader input = new BufferedReader(new InputStreamReader(
					System.in));
			String inputData = input.readLine();
			String[] data = inputData.split(",");

			if (Integer.parseInt(data[SIZE]) == 1) {
				makeSmallArea(axis, Integer.parseInt(data[X]),
						Integer.parseInt(data[Y]));
			}

			if (Integer.parseInt(data[SIZE]) == 2) {
				makeMiddleArea(axis, Integer.parseInt(data[X]),
						Integer.parseInt(data[Y]));

			}

			if (Integer.parseInt(data[SIZE]) == 3) {
				makeBigArea(axis, Integer.parseInt(data[X]),
						Integer.parseInt(data[Y]));

			}
			countAxis = 0;
			for (int countX = 0; countX < 10; countX++) {
				for (int countY = 0; countY < 10; countY++) {
					if (axis[countX][countY] == 0) {
						countAxis++;
					}
					if (axis[countX][countY] > maxThickness) {
						maxThickness = axis[countX][countY];
					}

				}
			}
			System.out.println(countAxis);
			System.out.println(maxThickness);

		}
	}

	static void makeSmallArea(int[][] axis, int xAxis, int yAxis) {
		axis[xAxis][yAxis]++;
		axis[xAxis][yAxis + 1]++;
		axis[xAxis + 1][yAxis]++;
		axis[xAxis][yAxis - 1]++;
		axis[xAxis - 1][yAxis]++;

	}

	static void makeMiddleArea(int[][] axis, int xAxis, int yAxis) {
		makeSmallArea(axis, xAxis, yAxis);
		axis[xAxis + 1][yAxis + 1]++;
		axis[xAxis + 1][yAxis - 1]++;
		axis[xAxis - 1][yAxis + 1]++;
		axis[xAxis - 1][yAxis - 1]++;

	}

	static void makeBigArea(int[][] axis, int xAxis, int yAxis) {
		makeMiddleArea(axis, xAxis, yAxis);
		axis[xAxis][yAxis + 2]++;
		axis[xAxis][yAxis - 2]++;
		axis[xAxis + 2][yAxis]++;
		axis[xAxis - 2][yAxis]++;

	}

}

import java.io.BufferedReader;
import java.io.InputStreamReader;

public class Main {
	private final static byte MASK_RIGHT = 0x0F; //4bit
	private final static int STRIP_LENGTH = 10; //1
	private static int[] punchTape = new int[100]; //10*10

	public static void main(String[] args) throws Exception {
		BufferedReader reader = new BufferedReader(new InputStreamReader(System.in), 256);
		String sLine;
		while ((sLine = reader.readLine()) != null) {
			droppingInk(sLine.charAt(0) & MASK_RIGHT, sLine.charAt(2) & MASK_RIGHT, sLine.charAt(4) & MASK_RIGHT);
		}
		calcResult();
	}

	/**
	 *
	 */
	private static void droppingInk(int x, int y, int size) {
		sinking(x, y - 1);
		sinking(x - 1, y);
		sinking(x, y);
		sinking(x + 1, y);
		sinking(x, y + 1);
		if (size == 1) {
			return;
		}

		sinking(x - 1, y - 1);
		sinking(x + 1, y - 1);
		sinking(x - 1, y + 1);
		sinking(x + 1, y + 1);
		if (size == 2) {
			return;
		}

		sinking(x, y - 2);
		sinking(x - 2, y);
		sinking(x + 2, y);
		sinking(x, y + 2);
	}

	/**
	 * 
	 */
	private static void sinking(int x, int y) {
		if (x >= 0 && x < 10 && y >= 0 && y < 10) {
			punchTape[x * STRIP_LENGTH + y]++;
		}
	}

	/**
	 *
	 */
	private static void calcResult() {
		int white = 0;//
		int max = 0;//
		for (int cell : punchTape) {
			if (cell == 0) {
				white++;
			} else if (cell > max) {
				max = cell;
			}
		}
		System.out.println(white);
		System.out.println(max);
	}
}

