			for (y = 2; y < 12; y++) {
				int cell = cells[x][y];
				if (cell == 0) {
					blindNum++;
				}
				if (max < cell) {
					max = cell;
				}
			}
		}
		System.out.println(blindNum + "\n" + max);
	}

	/**
	 * ??\??????????????????????????????????????????????<br>
	 *
	 * @param x
	 *            ???
	 * @param y
	 *            ???
	 * @param size
	 *            ?????????????
	 *
	 */
	private static void set(int x, int y, int size) {
		cells[x][y]++;
		if (size >= S) {
			cells[x - 1][y]++;
			cells[x + 1][y]++;
			cells[x][y - 1]++;
			cells[x][y + 1]++;
		}
		if (size >= M) {
			cells[x - 1][y - 1]++;
			cells[x - 1][y + 1]++;
			cells[x + 1][y - 1]++;
			cells[x + 1][y + 1]++;
		}
		if (size >= L) {
			cells[x - 2][y]++;
			cells[x + 2][y]++;
			cells[x][y - 2]++;
			cells[x][y + 2]++;
		}
	}
