	 *
	 * @param x
	 * @param y
	 */
	public static void addLarge(int x, int y) {
		if (y != 0 && y != 1) {
			droppingInkList.add(joinString(x, y - 2));
		}
		if (y != 8 && y != 9) {
			droppingInkList.add(joinString(x, y + 2));
		}
		if (x != 0 && x != 1) {
			droppingInkList.add(joinString(x - 2, y));
		}
		if (x != 8 && x != 9) {
			droppingInkList.add(joinString(x + 2, y));
		}
	}

	/**
	 * ???????????????????????????????????????????????????????
	 *
	 * @param list
	 * @return ??????????????????????????????????????
	 */
	public static int countWhiteCell(List<String> list) {
		HashSet<String> set = new HashSet<String>(list);
		// ????????????????????????????????????????????????????????????????????
		return CELL_COUNT - set.size();
	}

	/**
	 * ????????????????????????????????????????????????????????????????????????????????????????????????????
	 *
	 * @param list
	 * @return ??????????????????????????????
	 */
	public static int countMostDroppingInk(List<String> list) {
		Collections.sort(list);
		String beforeStr = "";

		// ??????????????????????????????
		int mostCount = 0;
		// ?????????????????????????????????????
		int count = 0;

		for (String str : list) {
			if (beforeStr.equals("")) {
				// ??????
				count++;
				beforeStr = str;
			} else if (!beforeStr.equals(str)) {
				// ????????????????????????????
				if (count > mostCount) {
					// ????????????????????????????????????????????????????????????????????????????????????????????????
					mostCount = count;
				}
				count = 1;
				beforeStr = str;
			} else {
				// ????????????????????????????????
				count++;
			}
		}

		if (count > mostCount) {
			// ???????????????????????????????????????????????????????????????????????????????????????????????????
			mostCount = count;
		}

		return mostCount;
	}

}

