	private static void sub(int x, int y, int s){
		if(x<0 || x>=10 || y<0 || y>=10) return ;
		board[x][y]++;
		for(int i=0; i<((s==0)?0:(s==1)?4:(s==2)?8:12);i++){
			sub(x+dx[i], y+dy[i], 0);
		}
		return ;
	}
}

