				}
			}
			else if (size == 3)
			{
				for (int i = 0; i < 5; i++)
				{
					for (int j = 0; j < 5; j++)
					{
						try
						{
							map[x-2+i][y-2+j] += drop3[i][j];
						}
						catch (ArrayIndexOutOfBoundsException e){continue;}
