    paper[x+2][y+2] +=1;
    paper[x+3][y+2] +=1;
    paper[x+2][y+3] +=1;
    return paper;
  }

  public static int[][] inkM(int x, int y, int paper[][]){
    paper = inkS(x, y, paper);
    paper[x+1][y+1] +=1;
    paper[x+3][y+1] +=1;
    paper[x+1][y+3] +=1;
    paper[x+3][y+3] +=1;
    return paper;
  }

  public static int[][] inkL(int x, int y, int paper[][]){
    paper = inkM(x, y, paper);
    paper[x+2][y  ] +=1;
    paper[x  ][y+2] +=1;
    paper[x+4][y+2] +=1;
    paper[x+2][y+4] +=1;
    return paper;
  }

