			}
			else if (size == 2) {
				map[y+1][x]++;
				map[y-1][x]++;
				map[y][x+1]++;
				map[y][x-1]++;
				map[y+1][x+1]++;
				map[y+1][x-1]++;
				map[y-1][x+1]++;
				map[y-1][x-1]++;
			}
			else if (size == 3) {
				map[y+1][x]++;
				map[y-1][x]++;
				map[y][x+1]++;
				map[y][x-1]++;
				map[y+1][x+1]++;
				map[y+1][x-1]++;
				map[y-1][x+1]++;
				map[y-1][x-1]++;
				map[y+2][x]++;
				map[y-2][x]++;
				map[y][x+2]++;
				map[y][x-2]++;
			}

		}

		int white = 0, color = 0;
		for (int i=2; i<=11; i++) {
			for (int j=2; j<=11; j++) {
				color = Math.max(color, map[i][j]);
				if (map[i][j] == 0) white++;
			}
		}

		out.println(white);
		out.println(color);

	}

	static boolean isOutside (int x, int y) {
			if (x<0 || y<0 || x>9 || y>9) return false;
			return true;
	}



	public static void main(String[] args) {
		FastScanner in = new FastScanner(System.in);
		PrintWriter out = new PrintWriter(System.out);
		Methods ms = new Methods();
		solve(in, out, ms);
		in.close();
		out.close();
	}

	static class Methods {

		public void print (Object... ar) {System.out.println(Arrays.deepToString(ar));}

		public void yesno (PrintWriter out, boolean b) {out.println(b?"Yes":"No");}

		public void YESNO (PrintWriter out, boolean b) {out.println(b?"YES":"NO");}

		public int max (int... ar) {Arrays.sort(ar); return ar[ar.length-1];}

		public int min (int... ar) {Arrays.sort(ar); return ar[0];}

		public long gcd (long a, long b) {return b>0?gcd(b,a%b):a;}

		public long lcm (long a, long b) {return a/gcd(a,b)*b;}

		public long factorial (int i) {
			if (i==1) return 1;
			else return i*factorial(i-1);
		}

		public int manhat (int x1, int y1, int x2, int y2) {
			return Math.abs(x1-x2)+Math.abs(y1-y2);
		}

		public double euclid (double x1, double y1, double x2, double y2) {
			return Math.sqrt((x2-x1)*(x2-x1)+(y2-y1)*(y2-y1));
		}

		public boolean isPrime (int n) {
			if (n==2) return true;
			if (n<2 || n%2==0) return false;
			for (int i=3; i<=Math.sqrt(n); i+=2) if(n%i==0) return false;
			return true;
		}

	}

	static class FastScanner {

		private InputStream in;
		private byte[] buffer = new byte[1024];
		private  int length = 0, p = 0;

		public FastScanner (InputStream stream) {
			in = stream;
		}

		public boolean hasNextByte () {
			if (p < length) return true;
			else {
				p = 0;
				try {length = in.read(buffer);}
				catch (Exception e) {e.printStackTrace();}
				if (length <= 0) return false;
			}
			return true;
		}

		public int readByte () {
			if (hasNextByte() == true) return buffer[p++];
			return -1;
		}

		public boolean isPrintable (int n) {return 33<=n&&n<=126;}

		public void skip () {
			while (hasNextByte() && !isPrintable(buffer[p])) p++;
		}

		public boolean hasNext () {skip(); return hasNextByte();}

		public String next () {
			if (!hasNext()) throw new NoSuchElementException();
			StringBuilder sb = new StringBuilder();
			int t = readByte();
			while (isPrintable(t)) {
				sb.appendCodePoint(t);
				t = readByte();
			}
			return sb.toString();
		}

		public String[] nextArray (int n) {
			String[] ar = new String[n];
			for (int i=0; i<n; i++) ar[i] = next();
			return ar;
		}

		public int nextInt () {return Math.toIntExact(nextLong());}

		public int[] nextIntArray (int n) {
			int[] ar = new int[n];
			for (int i=0; i<n; i++) ar[i] = nextInt();
			return ar;
		}

		public long nextLong () {
			if (!hasNext()) throw new NoSuchElementException();
			boolean minus = false;
			int temp = readByte();
			if (temp == '-') {
				minus = true;
				temp = readByte();
			}
			if (temp<'0' || '9'<temp) throw new NumberFormatException();
			long n = 0;
			while (isPrintable(temp)) {
				if ('0'<=temp && temp<='9') {
					n *= 10;
					n += temp - '0';
				}
				else throw new NumberFormatException();
				temp = readByte();
			}
			return minus? -n : n;
		}

		public long[] nextLongArray (int n) {
			long[] ar = new long[n];
			for (int i=0; i<n; i++) ar[i] = nextLong();
			return ar;
		}

		public double nextDouble () {
			return Double.parseDouble(next());
		}

		public double[] nextDoubleArray (int n) {
			double[] ar = new double[n];
			for (int i=0; i<n; i++) ar[i] = nextDouble();
			return ar;
		}

		public void close () {
			try {in.close();}
			catch(Exception e){}
		}

	}

}

import java.io.IOException;
import java.util.Scanner;

public class Main {

	private static Scanner sc;
	private static int[][] squares;

	static {
		sc = new Scanner(System.in);
		squares = new int[10][10];
	}

	public static void main(String[] args) throws IOException {

		String line;
		int x, y, size, blind = 100, max = 0;

		while ((line = sc.nextLine()) != null && !line.isEmpty()) {
			String[] values = line.split(",");
			x = Integer.parseInt(values[0]);
			y = Integer.parseInt(values[1]);
			size = Integer.parseInt(values[2]);
			set(x, y, size);
		}
		for (int[] square : squares) {
			for (int value : square) {
				if (value != 0) {
					blind--;
				}
				if (max < value) {
					max = value;
				}
			}
		}
		System.out.println(blind + "\n" + max);
	}

	private static void set(int x, int y, int size) {
		squares[x][y]++;
		switch (size) {
		case 1:
			set1(x, y);
			break;
		case 2:
			set1(x, y);
			set2(x, y);
			break;
		case 3:
			set1(x, y);
			set2(x, y);
			set3(x, y);
		default:
			break;
		}
	}

	private static void set1(int x, int y) {
		if (x > 0)
			squares[x - 1][y]++;
		if (x < 9)
			squares[x + 1][y]++;
		if (y > 0)
			squares[x][y - 1]++;
		if (y < 9)
			squares[x][y + 1]++;
	}

	private static void set2(int x, int y) {
		if (x > 0 && y > 0)
			squares[x - 1][y - 1]++;
		if (x > 0 && y < 9)
			squares[x - 1][y + 1]++;
		if (x < 9 && y > 0)
			squares[x + 1][y - 1]++;
		if (x < 9 && y < 10)
			squares[x + 1][y + 1]++;
	}

	private static void set3(int x, int y) {
		if (x - 1 > 0)
			squares[x - 2][y]++;
		if (x + 1 < 9)
			squares[x + 2][y]++;
		if (y - 1 > 0)
			squares[x][y - 2]++;
		if (y + 1 < 9)
			squares[x][y + 2]++;

	}
}

