    private int[] paper;
    private int droppedCount;
    private int maxDropping;

    private static final int SMALL_INK_DROP_SIZE = 3;
    private static final boolean[] SMALL_INK_DROP =
        {
           false, true, false,
           true , true, true,
           false, true, false
        };

    private static final int MEDIUN_INK_DROP_SIZE = 3;
    private static final boolean[] MEDIUM_INK_DROP =
        {
           true, true, true,
           true, true, true,
           true, true, true
        };

    private static final int LARGE_INK_DROP_SIZE = 5;
    private static final boolean[] LARGE_INK_DROP =
        {
           false, false, true, false, false,
           false, true,  true, true,  false,
           true,  true,  true, true,  true,
           false, true,  true, true,  false,
           false, false, true, false, false
        };


    public static void main(String args[]) throws IOException {
        Main app = new Main();

        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        String input = "";

        while(true) {
            input = br.readLine();
            if(input == null) break;

            String[] splittedStr = input.split(",");

            int x = Integer.parseInt(splittedStr[0]);
            int y = Integer.parseInt(splittedStr[1]);
            int dropSize = Integer.parseInt(splittedStr[2]);

            app.dropInk(x, y, dropSize);
        }

        System.out.println(PAPER_SIZE * PAPER_SIZE - app.getDroppedCount());
        System.out.println(app.getMaxDropping());

        //app.printPaper();
    }

    public Main() {
        this.paper = new int[PAPER_SIZE * PAPER_SIZE];
        this.droppedCount = 0;
        this.maxDropping = 0;
    }

    public void dropInk(int x, int y, int inkSize) {
        switch(inkSize) {
        case 1:
            dropInk(x, y, SMALL_INK_DROP_SIZE, SMALL_INK_DROP);
            break;
        case 2:
            dropInk(x, y, MEDIUN_INK_DROP_SIZE, MEDIUM_INK_DROP);
            break;
        case 3:
            dropInk(x, y, LARGE_INK_DROP_SIZE, LARGE_INK_DROP);
            break;
        }
    }

    private void dropInk(int x, int y, int dropSize, boolean[] drop) {
        for(int i = 0; i < dropSize; i++) {
            int tx = x - dropSize / 2 + i;
            if(tx < 0) continue;
            if(tx >= PAPER_SIZE) break;

            for(int j = 0; j < dropSize; j++) {
                if(!drop[i * dropSize + j]) continue;

                int ty = y - dropSize /2 + j;
                if(ty < 0) continue;
                if(ty >= PAPER_SIZE) break;

                if(this.paper[tx * PAPER_SIZE + ty]++ == 0 ) {
                    this.droppedCount++;
                }
                if(this.maxDropping < this.paper[tx * PAPER_SIZE + ty]) {
                    this.maxDropping = this.paper[tx * PAPER_SIZE + ty];
                }
            }
        }
        //printPaper();
    }

    public int getDroppedCount() { return this.droppedCount; }
    public int getMaxDropping() { return this.maxDropping; }

    public void printPaper() {
        for(int i = 0; i < PAPER_SIZE * PAPER_SIZE; i++) {
            System.out.print(paper[i] + ", ");
            if((i + 1) % PAPER_SIZE == 0)System.out.println();
        }
        System.out.println();
    }
}

import java.io.*;
import java.util.*;
import java.util.regex.Pattern;
import java.util.regex.Matcher;

class Main{

	int[][] paper = new int[10][10];

	public void new_paper(){
		for(int x=0;x<10;x++){
			for(int y=0;y<10;y++){
				paper[x][y]=0;
			}
		}
	}

	public void drop_small(int x1,int y1){
		paper[x1][y1]++;
		if(y1-1 >= 0)
			paper[x1][y1-1]++;
		if(x1-1 >= 0)
			paper[x1-1][y1]++;
		if(x1+1 < 10)
			paper[x1+1][y1]++;
		if(y1+1 < 10)
			paper[x1][y1+1]++;
	}

	public void drop_medium(int x1,int y1){
		drop_small(x1,y1);
		if(x1-1 >= 0 && y1-1 >= 0)
			paper[x1-1][y1-1]++;
		if(x1+1 < 10 && y1-1 >= 0)
			paper[x1+1][y1-1]++;
		if(x1-1 >= 0 && y1+1 < 10)
			paper[x1-1][y1+1]++;
		if(x1+1 < 10 && y1+1 < 10)
			paper[x1+1][y1+1]++;
	}

	public void drop_large(int x1,int y1){
		drop_medium(x1,y1);
		if(y1-2 >= 0)
			paper[x1][y1-2]++;
		if(x1-2 >= 0)
			paper[x1-2][y1]++;
		if(x1+2 < 10)
			paper[x1+2][y1]++;
		if(y1+2 < 10)
			paper[x1][y1+2]++;
	}

	public void drop_ink(ArrayList<Integer> list){
		int x1,y1,size;
		new_paper();

		for(int i=0;i<list.size()/3;i++){
			x1 = list.get(i*3);
			y1 = list.get(i*3+1);
			size = list.get(i*3+2);
			if(size == 1)drop_small(x1,y1);
			else if(size == 2)drop_medium(x1,y1);
			else drop_large(x1,y1);
		}
	}

	public void print_paper(){
		int count_zero,max;
		count_zero = 0;
		max = 0;

		for(int x=0;x<10;x++){
			for(int y=0;y<10;y++){
				if(max < paper[x][y])
					max = paper[x][y];
			}
		}

		for(int x=0;x<10;x++){
			for(int y=0;y<10;y++){
				if(paper[x][y] == 0)
					count_zero++;
			}

		}
		System.out.println(count_zero);
		System.out.println(max);
	}

	public static void main(String args[]) throws IOException{
		String str;
		ArrayList <Integer> inp = new ArrayList<Integer>();

		Scanner scan = new Scanner(System.in);

		while(scan.hasNext()){

			str = scan.next();

			String [] strAry = new String[str.length()];
			strAry = str.split(",");
			for(int j=0;j<strAry.length;j++){
				Pattern p = Pattern.compile("[0-9]*");
				Matcher m = p.matcher(strAry[j]);
				if(m.find()){
					int x = Integer.valueOf(m.group(0)).intValue();
					inp.add(x);
				}
			}
		}

		Main object = new Main();
		object.drop_ink(inp);
		object.print_paper();
	}
}

import java.io.BufferedReader;
import java.io.InputStreamReader;

public class Main {
	private final static byte MASK_RIGHT = 0x0F; //4bit
	private final static int STRIP_LENGTH = 10; //1
	private static int[] punchTape = new int[100]; //10*10

	public static void main(String[] args) throws Exception {
		BufferedReader reader = new BufferedReader(new InputStreamReader(System.in), 256);
		String sLine;
		while ((sLine = reader.readLine()) != null) {
			droppingInk(sLine.charAt(0) & MASK_RIGHT, sLine.charAt(2) & MASK_RIGHT, sLine.charAt(4) & MASK_RIGHT);
		}
		calcResult();
	}

	/**
	 *
	 */
	private static void droppingInk(int x, int y, int size) {
		sinking(x, y - 1);
		sinking(x - 1, y);
		sinking(x, y);
		sinking(x + 1, y);
		sinking(x, y + 1);
		if (size == 1) {
			return;
		}

		sinking(x - 1, y - 1);
		sinking(x + 1, y - 1);
		sinking(x - 1, y + 1);
		sinking(x + 1, y + 1);
		if (size == 2) {
			return;
		}

		sinking(x, y - 2);
		sinking(x - 2, y);
		sinking(x + 2, y);
		sinking(x, y + 2);
	}

	/**
	 * 
	 */
	private static void sinking(int x, int y) {
		if (x >= 0 && x < 10 && y >= 0 && y < 10) {
			punchTape[x * STRIP_LENGTH + y]++;
		}
	}

	/**
	 *
	 */
	private static void calcResult() {
		int white = 0;//
		int max = 0;//
		for (int cell : punchTape) {
			if (cell == 0) {
				white++;
			} else if (cell > max) {
				max = cell;
			}
		}
		System.out.println(white);
		System.out.println(max);
	}
}

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

public class Main {

	/**  */
	static final String REGEX = ",";

	/**  */
	static final int SMALL_SIZE = 1;

	/**  */
	static final int MIDIAM_SIZE = 2;

	/**  */
	static final int LARGE_SIZE = 3;

	/**
	 * @param args
	 * @throws IOException
	 */
	public static void main(String[] args) throws IOException {
		BufferedReader reader = new BufferedReader(new InputStreamReader(
				System.in));
		int[] field = new int[101];
		int x;
		int y;
		int size;
		while (true) {
			String line = reader.readLine();
			if (line == null || line.isEmpty()) {
				break;
			}
			String[] str = line.split(REGEX);
			x = Integer.valueOf(str[0]);
			y = Integer.valueOf(str[1]);
			size = Integer.valueOf(str[2]);

			switch (size) {
			case SMALL_SIZE:
				field[calcIndex(x - 1, y)] += 1;
				field[calcIndex(x, y + 1)] += 1;
				field[calcIndex(x, y)] += 1;
				field[calcIndex(x, y - 1)] += 1;
				field[calcIndex(x + 1, y)] += 1;
				break;
			case MIDIAM_SIZE:
				field[calcIndex(x - 1, y + 1)] += 1;
				field[calcIndex(x - 1, y)] += 1;
				field[calcIndex(x - 1, y - 1)] += 1;
				field[calcIndex(x, y + 1)] += 1;
				field[calcIndex(x, y)] += 1;
				field[calcIndex(x, y - 1)] += 1;
				field[calcIndex(x + 1, y + 1)] += 1;
				field[calcIndex(x + 1, y)] += 1;
				field[calcIndex(x + 1, y - 1)] += 1;
				break;
			case LARGE_SIZE:
				field[calcIndex(x - 2, y)] += 1;
				field[calcIndex(x - 1, y + 1)] += 1;
				field[calcIndex(x - 1, y)] += 1;
				field[calcIndex(x - 1, y - 1)] += 1;
				field[calcIndex(x, y + 2)] += 1;
				field[calcIndex(x, y + 1)] += 1;
				field[calcIndex(x, y)] += 1;
				field[calcIndex(x, y - 1)] += 1;
				field[calcIndex(x, y - 2)] += 1;
				field[calcIndex(x + 1, y + 1)] += 1;
				field[calcIndex(x + 1, y)] += 1;
				field[calcIndex(x + 1, y - 1)] += 1;
				field[calcIndex(x + 2, y)] += 1;
				break;
			default:
			}
		}

		int count = 0;
		int count1 = 0;
		int max = 0;
		for (int i = 0; i < 100; i++) {
			if (field[i] == 0) {
				count++;
			} else {
				count1++;
			}
			if (field[i] > max) {
				max = field[i];
			}
		}

		System.out.println(count);
		System.out.println(max);
	}

	/**
	 * xy
	 *
	 * @param x x
	 * @param y y
	 * @return 
	 */
	private static int calcIndex(int x, int y) {
		if (x < 0 || y < 0 || x > 9 || y > 9) {
			return 100;
		}
		return (x * 10) + y;
	}
}

import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);

        int[][] paper = new int[10][10];
        for (int i = 0; i < 10; i++) {
            for (int j = 0; j < 10; j++) {
                paper[i][j] = 0;
            }
        }

        while (sc.hasNext()) {
            String S = sc.next();
            int x = Integer.parseInt(S.split(",")[0]);
            int y = Integer.parseInt(S.split(",")[1]);
            int s = Integer.parseInt(S.split(",")[2]);

            switch (s) {
                case 1:
                    paper = smallInk(x, y, paper);
                    break;
                case 2:
                    paper = mediumInk(x, y, paper);
                    break;
                case 3:
                    paper = bigInk(x, y, paper);
                    break;
            }
        }

        int none = 0;
        int max = Integer.MIN_VALUE;
        for (int i = 0; i < 10; i++) {
            for (int j = 0; j < 10; j++) {
                max = (paper[i][j] > max)? paper[i][j] : max;
                none = (paper[i][j] == 0)? none + 1 : none;
            }
        }
        System.out.println(none);
        System.out.println(max);
    }

    private static int[][] smallInk(int x, int y, int[][] paper) {
        if (x - 1 >= 0) paper[x-1][y]++;
        if (x + 1 < 10) paper[x+1][y]++;
        if (y - 1 >= 0) paper[x][y-1]++;
        if (y + 1 < 10) paper[x][y+1]++;

        return paper;
    }

    private static int[][] mediumInk(int x, int y, int[][] paper) {
        if (x - 1 >= 0) paper[x-1][y]++;
        if (x + 1 < 10) paper[x+1][y]++;
        if (y - 1 >= 0) paper[x][y-1]++;
        if (y + 1 < 10) paper[x][y+1]++;
        if (x - 1 >= 0 && y - 1 >= 0) paper[x-1][y-1]++;
        if (x - 1 >= 0 && y + 1 < 10) paper[x-1][y+1]++;
        if (x + 1 < 10 && y - 1 >= 0) paper[x+1][y-1]++;
        if (x + 1 < 10 && y + 1 < 10) paper[x+1][y+1]++;

        return paper;
    }

    private static int[][] bigInk(int x, int y, int[][] paper) {
        if (x - 1 >= 0) paper[x-1][y]++;
        if (x + 1 < 10) paper[x+1][y]++;
        if (y - 1 >= 0) paper[x][y-1]++;
        if (y + 1 < 10) paper[x][y+1]++;
        if (x - 1 >= 0 && y - 1 >= 0) paper[x-1][y-1]++;
        if (x - 1 >= 0 && y + 1 < 10) paper[x-1][y+1]++;
        if (x + 1 < 10 && y - 1 >= 0) paper[x+1][y-1]++;
        if (x + 1 < 10 && y + 1 < 10) paper[x+1][y+1]++;
        if (x - 2 >= 0) paper[x-2][y]++;
        if (x + 2 < 10) paper[x+2][y]++;
        if (y - 2 >= 0) paper[x][y-2]++;
        if (y + 2 < 10) paper[x][y+2]++;

        return paper;
    }
}

