			if(data.isEmpty())
				break;
			StringTokenizer tokenizer = new StringTokenizer(data, ",");
			int x = Integer.parseInt(tokenizer.nextToken()) + 2;
			int y = Integer.parseInt(tokenizer.nextToken()) + 2;
			int size = Integer.parseInt(tokenizer.nextToken());

			nums[x][y]++;
			nums[x - 1][y]++;
			nums[x][y - 1]++;
			nums[x][y + 1]++;
			nums[x + 1][y]++;
			if (size == 2 || size == 3) {
				nums[x - 1][y - 1]++;
				nums[x - 1][y + 1]++;
				nums[x + 1][y - 1]++;
				nums[x + 1][y + 1]++;
			}
