public class Main {
	public static void main(String[] args) throws IOException {
		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
		String str = br.readLine();
		int[][] density = new int[10][10];
		for (int i = 0; i < 10; i++) {
			for (int j = 0; j < 10; j++) {
				density[i][j] = 0;
			}
		}
		while (str!=null) {
			String[] dataset = str.split(",");
			int x = Integer.parseInt(dataset[0]);
			int y = Integer.parseInt(dataset[1]);
			int s = Integer.parseInt(dataset[2]);
			density[x][y] += 1;
			if (x - 1 >= 0)
				density[x - 1][y] += 1;
			if (x + 1 <= 10)
				density[x + 1][y] += 1;
			if (y - 1 >= 0)
				density[x][y - 1] += 1;
			if (y + 1 <= 10)
				density[x][y + 1] += 1;
			if (s >= 2) {
				if (x - 1 >= 0 && y - 1 >= 0)
					density[x - 1][y - 1] += 1;
				if (x - 1 >= 0 && y + 1 <= 10)
					density[x - 1][y + 1] += 1;
				if (x + 1 <= 10 && y - 1 >= 0)
					density[x + 1][y - 1] += 1;
				if (x + 1 <= 10 && y + 1 <= 10)
					density[x + 1][y + 1] += 1;
			}
			if (s == 3) {
				if (x - 2 >= 0)
					density[x - 2][y] += 1;
				if (x + 2 <= 10)
					density[x + 2][y] += 1;
				if (y - 2 >= 0)
					density[x][y - 2] += 1;
				if (y + 2 <= 10)
					density[x][y + 2] += 1;
			}
			str = br.readLine();
		}
		int cnt = 0;
		int max = 0;
		for (int[] column : density) {
			for (int densities : column) {
				if (densities > max)
					max = densities;
				else if (densities == 0)
					cnt++;
			}
		}
		System.out.println(cnt);
		System.out.println(max);
	}
}

import java.util.Arrays;
import java.util.Scanner;

public class Main {
	private int[][] paper = new int[10][10];

	public static void main(String[] args) {
		new Main().run();
	}

	private void run() {
		Scanner scan = new Scanner(System.in);

		for (int i = 0; i < 10; i++) Arrays.fill(paper[i], 0);

		while (scan.hasNext()) {
			String str = scan.nextLine();
			int x = Integer.parseInt(str.substring(0, 1));
			int y = Integer.parseInt(str.substring(2, 3));
			int s = Integer.parseInt(str.substring(4, 5));

			if(!out(x, y)) paper[x][y]++;
			if (s == 3) {
				large(x,y);
			}else if (s == 2) {
				medium(x, y);
			}else {
				small(x, y);
			}
		}
		int count = 0, max = -1;
		for (int i = 0; i < 10; i++) {
			for (int j = 0; j < 10; j++) {
				if (paper[i][j] == 0) count++;
				max = Math.max(max, paper[i][j]);
			}
		}
		System.out.println(count);
		System.out.println(max);

	}

	private void large(int x, int y) {
		medium(x, y);
		if(!out(x, y)) paper[x][y - 2]++;
		if(!out(x, y)) paper[x][y + 2]++;
		if(!out(x, y)) paper[x - 2][y]++;
		if(!out(x, y)) paper[x + 2][y]++;
	}

	private void medium(int x, int y) {
		small(x, y);
		if(!out(x, y)) paper[x - 1][y - 1]++;
		if(!out(x, y)) paper[x - 1][y + 1]++;
		if(!out(x, y)) paper[x + 1][y - 1]++;
		if(!out(x, y)) paper[x + 1][y + 1]++;
	}

	private void small(int x, int y) {
		if(!out(x, y)) paper[x][y - 1]++;
		if(!out(x, y)) paper[x][y + 1]++;
		if(!out(x, y)) paper[x - 1][y]++;
		if(!out(x, y)) paper[x + 1][y]++;
	}

	private boolean out(int x, int y) {
		if (x >= 0 && y >= 0 && x < 10 && y < 10) {
			return false;
		}else {
			return true;
		}
	}

	private void debug() {
		System.out.println();
	}

}

