import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.ArrayList;
import java.util.List;
import java.util.StringTokenizer;

public class Main {

	// 
	static int droppingField[][] = new int[10][10];
	// 
	static int clearCount = 0;
	// 
	static int maxDoropping = 0;

	public static void main(String[] args) throws NumberFormatException,
			IOException {

		// 
		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
		List<int[]> inkList = new ArrayList<int[]>();

		// 
		String line = "";
		while ((line = br.readLine()) != null) {

			// 1
			StringTokenizer st = new StringTokenizer(line, ",");

			// 
			while (st.hasMoreTokens()) {
				int[] input = { Integer.parseInt(st.nextToken()),
						Integer.parseInt(st.nextToken()),
						Integer.parseInt(st.nextToken()) };
				inkList.add(input);
			}
		}
		br.close();

		// 
		drop(inkList, droppingField);

		//  
		outputResult();

		//  
		System.out.println(clearCount);
		System.out.println(maxDoropping);

	}

	// 
	public static void drop(List<int[]> inkList, int[][] droppingField) {

		List<int[]> inks = new ArrayList<int[]>();
		int maxLength = droppingField.length;

		// 
		for (int[] a : inkList) {

			if (a[2] == 1) {
				inks.addAll(smallInk(a[0], a[1], maxLength));
			} else if (a[2] == 2) {
				inks.addAll(middiumInk(a[0], a[1], maxLength));
			} else if (a[2] == 3) {
				inks.addAll(largeInk(a[0], a[1], maxLength));
			}

		}
		droppingInk(inks);

	}

	// 
	public static List<int[]> smallInk(int x, int y, int maxLength) {
		List<int[]> inks = new ArrayList<int[]>();

		int[] ink1 = { x, y };
		int[] ink2 = { x - 1, y };
		int[] ink3 = { x + 1, y };
		int[] ink4 = { x, y - 1 };
		int[] ink5 = { x, y + 1 };

		inks.add(ink1);
		inks.add(ink2);
		inks.add(ink3);
		inks.add(ink4);
		inks.add(ink5);

		return inks;
	}

	// 
	public static List<int[]> middiumInk(int x, int y, int maxLength) {
		List<int[]> inks = new ArrayList<int[]>();

		for (int i = x - 1; i <= x + 1; i++) {
			for (int n = y - 1; n <= y + 1; n++) {
				int[] ink1 = { i, n };
				inks.add(ink1);
			}
		}

		return inks;

	}

	// 
	public static List<int[]> largeInk(int x, int y, int maxLength) {
		List<int[]> inks = new ArrayList<int[]>();

		for (int i = x - 1; i <= x + 1; i++) {
			for (int n = y - 1; n <= y + 1; n++) {
				int[] ink1 = { i, n };
				inks.add(ink1);
			}
		}
		int[] ink2 = { x - 2, y };
		int[] ink3 = { x + 2, y };
		int[] ink4 = { x, y - 2 };
		int[] ink5 = { x, y + 2 };

		inks.add(ink2);
		inks.add(ink3);
		inks.add(ink4);
		inks.add(ink5);

		return inks;
	}

	// 
	public static void droppingInk(List<int[]> inks) {
		int maxLength = droppingField.length;

		for (int[] a : inks) {
			if (a[0] >= 0 && a[0] < maxLength && a[1] >= 0 && a[1] < maxLength) {
				droppingField[a[0]][a[1]]++;
			}
		}

	}

	//  
	public static void outputResult() {

		for (int i = 0; i < droppingField.length; i++) {
			for (int n = 0; n < droppingField[0].length; n++) {

				if (droppingField[i][n] == 0) {
					// (0)
					clearCount++;
				} else {
					// 
					maxDoropping = Math.max(maxDoropping, droppingField[i][n]);
				}
			}
		}

	}

}

import java.io.BufferedReader;
import java.io.InputStreamReader;

public class Main {
	private final byte MASK_RIGHT = 0x0F;//4bit
	private int[][] paper = new int[10][10];//

	public static void main(String[] args) throws Exception {
		new Main().execute();
	}

	private void execute() throws Exception {
		BufferedReader reader = new BufferedReader(new InputStreamReader(System.in), 128);
		String sLine;
		while ((sLine = reader.readLine()) != null) {
			droppingInk(sLine.charAt(0) & MASK_RIGHT, sLine.charAt(2) & MASK_RIGHT, sLine.charAt(4) & MASK_RIGHT);
		}
		calcResult();
	}

	/**
	 *
	 */
	private void droppingInk(int x, int y, int size) {
		sinking(x, y - 1);
		sinking(x - 1, y);
		sinking(x, y);
		sinking(x + 1, y);
		sinking(x, y + 1);
		if (size == 1) {
			return;
		}

		sinking(x - 1, y - 1);
		sinking(x + 1, y - 1);
		sinking(x - 1, y + 1);
		sinking(x + 1, y + 1);
		if (size == 2) {
			return;
		}

		sinking(x, y - 2);
		sinking(x - 2, y);
		sinking(x + 2, y);
		sinking(x, y + 2);
	}

	/**
	 * 
	 */
	private void sinking(int x, int y) {
		if (x >= 0 && x < 10 && y >= 0 && y < 10) {
			paper[x][y]++;
		}
	}

	/**
	 *
	 */
	private void calcResult() {
		int white = 0;//
		int max = 0;//
		for (int[] row : paper) {
			for (int colum : row) {
				if (colum == 0) {
					white++;
				} else if (colum > max) {
					max = colum;
				}
			}
		}
		System.out.println(white);
		System.out.println(max);
	}
}

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;
import java.util.Scanner;

public class Main{

    public static void main(String[] args) {

        exec();
    }

    private static void exec() {

        Scanner sc = new Scanner(System.in);
        List<String> mapPointList = new ArrayList<String>();
        int[][] dropPoint = null; // 

        while (sc.hasNextInt()) {

            int x = sc.nextInt(); // x
            int y = sc.nextInt(); // y
            int size = sc.nextInt(); // 

            // 
            if (size == 1) {
                dropPoint = smallDrop(x, y);
            } else if (size == 2) {
                dropPoint = middleDrop(x, y);
            } else if (size == 3) {
                dropPoint = largeDrop(x, y);
            }

            // 
            mapPointList = writePointMapArray(mapPointList, dropPoint);

            // 
            int[] result = writeDeepPoint(mapPointList);

            System.out.println(result[0]);
            System.out.println(result[1]);
        }
    }

    private static List<String> writePointMapArray(List<String> mapPointList, int[][] pointArray) {

        int index = 0;

        for (int point : pointArray[0]) {

            if ((point >= 0 && point < 10)
                && (pointArray[1][index] >= 0 && pointArray[1][index] < 10)) {

                String mapPoint = String.valueOf(point) + String.valueOf(pointArray[1][index]);
                mapPointList.add(mapPoint);
            }

            index++;
        }

        return mapPointList;
    }

    private static int[] writeDeepPoint(List<String> mapPointList) {

        Map<String, Integer> pointMap = new HashMap<String, Integer>();
        int deepPoint = 1;

        // 
        for (String point : mapPointList) {

            if (pointMap.containsKey(point)) {

                Integer number = pointMap.get(point);

                pointMap.remove(point);
                pointMap.put(point, number + 1);

                if (deepPoint < pointMap.get(point)) {

                    deepPoint = pointMap.get(point);
                }

            } else {

                pointMap.put(point, 1);
            }
        }

        // 
        int whitePoint = 100 - pointMap.size();

        int[] result = new int[] { whitePoint, deepPoint };

        return result;
    }

    private static int[][] largeDrop(int x, int y) {

        int[][] largeDropPointArray;
        largeDropPointArray = new int[2][];

        largeDropPointArray[0] =
            new int[] { x, x - 1, x, x + 1, x - 2, x - 1, x, x + 1, x + 2, x - 1, x, x + 1, x };
        largeDropPointArray[1] =
            new int[] { y - 2, y - 1, y - 1, y - 1, y, y, y, y, y, y + 1, y + 1, y + 1, y + 2 };

        return largeDropPointArray;
    }

    private static int[][] middleDrop(int x, int y) {

        int[][] middleDropPointArray;
        middleDropPointArray = new int[2][];

        middleDropPointArray[0] = new int[] { x - 1, x, x + 1, x - 1, x, x + 1, x - 1, x, x + 1 };
        middleDropPointArray[1] = new int[] { y - 1, y - 1, y - 1, y, y, y, y + 1, y + 1, y + 1 };

        return middleDropPointArray;
    }

    private static int[][] smallDrop(int x, int y) {

        int[][] smallDropPointArray;
        smallDropPointArray = new int[2][];

        smallDropPointArray[0] = new int[] { x, x - 1, x, x + 1, x };
        smallDropPointArray[1] = new int[] { y - 1, y, y, y, y + 1 };

        return smallDropPointArray;
    }
}

