			if (x - 1 >= 0)
				density[x - 1][y] += 1;
			if (x + 1 < 10)
				density[x + 1][y] += 1;
			if (y - 1 >= 0)
				density[x][y - 1] += 1;
			if (y + 1 < 10)
				density[x][y + 1] += 1;
			if (s >= 2) {
				if (x - 1 >= 0 && y - 1 >= 0)
					density[x - 1][y - 1] += 1;
				if (x - 1 >= 0 && y + 1 < 10)
					density[x - 1][y + 1] += 1;
				if (x + 1 < 10 && y - 1 >= 0)
					density[x + 1][y - 1] += 1;
				if (x + 1 < 10 && y + 1 < 10)
					density[x + 1][y + 1] += 1;
			}
			if (s == 3) {
				if (x - 2 >= 0)
					density[x - 2][y] += 1;
				if (x + 2 < 10)
					density[x + 2][y] += 1;
