import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

public class Main {

	public static void main(String[] args) throws IOException {
		// 10*10
		int paper[][] = new int[10][10];
		// 
		int emptySpace = 0;
		// 1
		int max = 0;

		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
		String str = br.readLine();

		while (str != null) {
			// (","),
			String[] array = str.split(",");
			int x = Integer.parseInt(array[0]);
			int y = Integer.parseInt(array[1]);
			int size = Integer.parseInt(array[2]);

			drop(paper, x, y, size);
		}

		// 1
		for (int i = 0; i < 10; i++) {
			for (int j = 0; j < 10; j++) {
				if (paper[i][j] == 0) {
					emptySpace++;
				}else if (paper[i][j] > max) {
					max = paper[i][j];
				}
			}
		}

		System.out.println(emptySpace);
		System.out.println(max);
	}

	// 
	private static void drop(int paper[][], int x, int y, int size) {
		if (size == 1) {
			drop1(paper, x, y);
		}
		if (size == 2) {
			drop2(paper, x, y);
		}
		if (size == 3) {
			drop3(paper, x, y);
		}
	}

	// 
	private static void drop1(int paper[][], int x, int y) {
		paper[x][y]++;
		if (x + 1 < 10) {
			paper[x + 1][y]++;
		}
		if (x - 1 > -1) {
			paper[x - 1][y]++;
		}
		if (y + 1 < 10) {
			paper[x][y + 1]++;
		}
		if (y - 1 > -1) {
			paper[x][y - 1]++;
		}
	}

	// 
	private static void drop2(int paper[][], int x, int y) {
		drop1(paper, x, y);
		if (x + 1 < 10 && y + 1 < 10) {
			paper[x + 1][y + 1]++;
		}
		if (x + 1 < 10 && y - 1 > -1) {
			paper[x + 1][y - 1]++;
		}
		if (x - 1 > -1 && y + 1 < 10) {
			paper[x - 1][y + 1]++;
		}
		if (x - 1 > -1 && y - 1 > -1) {
			paper[x - 1][y - 1]++;
		}
	}

	// 
	private static void drop3(int paper[][], int x, int y) {
		drop2(paper, x, y);
		if (x + 2 < 10) {
			paper[x + 2][y]++;
		}
		if (x - 2 > -1) {
			paper[x - 2][y]++;
		}
		if (y + 2 < 10) {
			paper[x][y + 2]++;
		}
		if (y - 2 > -1) {
			paper[x][y - 2]++;
		}
	}
}

