import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

public class Main {

	/**  */
	private static int[][] paper = new int[10][10];

	/**  */
	private final static String INK_SIZE_S = "1";

	/**  */
	private final static String INK_SIZE_M = "2";

	/**  */
	private final static String INK_SIZE_L = "3";

	/** 100 */
	private static int plainCells = 100;

	/**  */
	private static int maximumValue = 0;

	/**
	 *
	 *
	 * @param args
	 *            
	 * @throws IOException
	 */
	public static void main(String[] args) throws IOException {

		// 
		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));

		// 
		String line = null;
		while ((line = br.readLine()) != null) {

			String[] inkInfo = line.split(",");
			int x = Integer.parseInt(inkInfo[0]);
			int y = Integer.parseInt(inkInfo[1]);
			String size = inkInfo[2];

			// 
			dropInk(x, y); // 
			dropInk(x - 1, y); // 
			dropInk(x + 1, y); // 
			dropInk(x, y - 1); // 
			dropInk(x, y + 1); // 

			if (INK_SIZE_S.equals(size)) {
				continue;
			}

			// 
			dropInk(x - 1, y - 1); // 
			dropInk(x - 1, y + 1); // 
			dropInk(x + 1, y - 1); // 
			dropInk(x + 1, y + 1); // 

			if (INK_SIZE_M.equals(size)) {
				continue;
			}

			// 
			dropInk(x - 2, y); // 2
			dropInk(x + 2, y); // 2
			dropInk(x, y - 2); // 2
			dropInk(x, y + 2); // 2
		}

		// 
		System.out.println(plainCells); // 
		System.out.println(maximumValue); // 
	}

	/**
	 * <p>
	 *  +1</br>
	 * 01<br/>
	 * <br/>
	 * </p>
	 *
	 * @param x
	 *            x
	 * @param y
	 *            y
	 */
	private static void dropInk(int x, int y) {

		if (0 <= x && x < 10 && 0 <= y && y < 10) {
			if (paper[x][y] == 0) {
				plainCells -= 1;
			}

			paper[x][y] += 1;

			if (paper[x][y] > maximumValue) {
				maximumValue = paper[x][y];
			}

		}
	}

}

import java.util.*;
import java.io.*;

class Main {

    static final int SMALL = 1;
    static final int MIDDLE = 2;
    static final int LARGE = 3;

    public static void main( String[] args ) {

	final Scanner stdin = new Scanner( System.in );

	int[][] map = new int[10][10];

	while ( stdin.hasNextLine() ) {
	    String[] input = stdin.nextLine().split(",");
	    final int x = Integer.parseInt( input[0] );
	    final int y = Integer.parseInt( input[1] );
	    final int size = Integer.parseInt( input[2] );
	    switch ( size ) {
	    case SMALL:
		dropSmall( map, x, y );
		break;
	    case MIDDLE:
		dropMiddle( map, x, y );
		break;
	    case LARGE:
		dropLarge( map, x, y );
		break;
	    }
	}

	final int noOfZero = countZero( map );
	final int max = countMax( map );
	System.out.println(noOfZero);
	System.out.println(max);

    }

    static int countZero( int[][] map ) {
	int count = 0;
	for ( int i = 0; i < 10; i++ ) {
	    for ( int j = 0; j < 10; j++ ) {
		if ( map[i][j] == 0 ) {
		    count++;
		}
	    }
	}
	return count;
    }

    static int countMax( int[][] map ) {
	int max = 0;
	for ( int i = 0; i < 10; i++ ) {
	    for ( int j = 0; j < 10; j++ ) {
		if ( map[i][j] > max ) {
		    max = map[i][j];
		}
	    }
	}
	return max;
    }

    static boolean check( int val ) {
	return 0 <= val && val <= 9;
    }

    static void dropSmall( int[][] map, int x, int y ) {
	final int[] width = {0, 1, 0};
	final int[] yindex = {-1, 0, 1};

	for ( int i = 0; i < 3; i++ ) {
	    final int cy = y + yindex[i];
	    for ( int j = x - width[i]; j <= x + width[i]; j++ ) {
		if ( check( cy ) && check( j ) ) {
		    map[cy][j]++;
		}
	    }		
	}

    }

    static void dropMiddle( int[][] map, int x, int y ) {
	final int xs = Math.max( 0, x - 1 );
	final int xe = Math.min( 9, x + 1 );
	final int ys = Math.max( 0, y - 1 );
	final int ye = Math.min( 9, y + 1 );

	for ( int i = ys; i <= ye; i++ ) {
	    for ( int j = xs; j <= xe; j++ ) {
		map[i][j]++;
	    }
	}
    }

    static void dropLarge( int[][] map, int x, int y ) {
	final int[] width = {0, 1, 2, 1, 0};
	final int[] yindex = {-2, -1, 0, 1, 2};

	for ( int i = 0; i < 5; i++ ) {
	    final int cy = y + yindex[i];
	    for ( int j = x - width[i]; j <= x + width[i]; j++ ) {
		if ( check( cy ) && check( j ) ) {
		    map[cy][j]++;
		}
	    }		
	}
    }

}

