import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

public class Main {

    /**
     * 
     */
    private final static int SIZE = 10;

    public static void main(String[] args) throws IOException {
        //
        int blank = 0;

        //
        int depth = 0;

        //
        int[][] paper = createPaper(SIZE);

        //
        BufferedReader bufferedReader = new BufferedReader(
                new InputStreamReader(System.in));

        String line;

        while ((line = bufferedReader.readLine()) != null) {
            if (line.equals("")) {
                break;
            }
            String[] target = line.split(",", 0);

            int x = Integer.parseInt(target[0]);
            int y = Integer.parseInt(target[1]);
            int inkType = Integer.parseInt(target[2]);

            if (inkType == 1) {
                paper = dropInkSmall(paper, x, y);
            } else if (inkType == 2) {
                paper = dropInkMedium(paper, x, y);
            } else if (inkType == 3) {
                paper = dropInkLarge(paper, x, y);
            }
        }
        bufferedReader.close();
        for (int i = 0; i < paper.length; i++) {
            for (int j = 0; j < paper[i].length; j++) {
                if (paper[i][j] == 0) {
                    blank++;
                }
                if (depth < paper[i][j]) {
                    depth = paper[i][j];
                }
            }
        }
        System.out.println(blank);
        System.out.println(depth);
    }

    public static int[][] dropInkSmall(int[][] paper, int x, int y) {
        //
        paper[x][y] += 1;
        //
        if (0 <= (x - 1)) {
            paper[x - 1][y] += 1;
        }
        //
        if ((x + 1) <= 9) {
            paper[x + 1][y] += 1;
        }
        //
        if (0 <= (y - 1)) {
            paper[x][y - 1] += 1;
        }
        //
        if ((y + 1) <= 9) {
            paper[x][y + 1] += 1;
        }

        return paper;

    }

    public static int[][] dropInkMedium(int[][] paper, int x, int y) {

        //
        paper = dropInkSmall(paper, x, y);
        //

        //
        if (0 <= (x - 1) && 0 <= (y - 1)) {
            paper[x - 1][y - 1] += 1;
        }
        //
        if ((x + 1) <= 9 && 0 <= (y - 1)) {
            paper[x + 1][y - 1] += 1;
        }
        //
        if (0 <= (x - 1) && (y + 1) <= 9) {
            paper[x - 1][y + 1] += 1;
        }
        //
        if ((x + 1) <= 9 && (y + 1) <= 9) {
            paper[x + 1][y + 1] += 1;
        }

        return paper;

    }

    public static int[][] dropInkLarge(int[][] paper, int x, int y) {

        paper = dropInkMedium(paper, x, y);

        //
        if (0 <= (x - 2)) {
            paper[x - 2][y] += 1;
        }
        //
        if ((x + 2) <= 9) {
            paper[x + 2][y] += 1;
        }
        //
        if (0 <= (y - 2)) {
            paper[x][y - 2] += 1;
        }
        //
        if ((y + 2) <= 9) {
            paper[x][y + 2] += 1;
        }

        return paper;

    }

    public static int[][] createPaper(int size) {
        return new int[size][size];
    }

}

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

public class Main{
	public static void main(String[] args)throws IOException{
		int[][] map = new int[10][10];
		BufferedReader br = null;
		int max = 0,num = 0;
		try{
			br = new BufferedReader(new InputStreamReader(System.in));
			String buf;
			while((buf = br.readLine())!=null){

				String[] data = buf.split(",");
				int x = new Integer(data[0]);
				int y = new Integer(data[1]);
				int point = new Integer(data[2]);
				switch(point){
				case 3:
					for(int i = -2; i <=2; i+=4){
							if(check(x+i ,y))
									map[x+i][y]++;
							if(check(x,y+i))
								map[x][y+i]++;
					}
				case 2:
					for(int i = -1 ; i <=1; i+=2){
						for(int j = -1 ; j <=1 ;j +=2){
							if(check(x+i,y+j))
								map[x+i][y+j]++;
						}
					}
				case 1:
					for(int i = -1; i <=1; i+=2){
						if(check(x+i ,y))
								map[x+i][y]++;
						if(check(x,y+i))
							map[x][y+i]++;
					}
					map[x][y]++;
				}
			}
			for(int i = 0; i < map.length;i++){
				for(int j = 0; j < map[i].length;j++){
					if(map[i][j] == 0)num++;
					max = Math.max(max,map[i][j]);
				}
			}

		}finally{
			br.close();
		}
		System.out.println(num);
		System.out.println(max);
	}
		static boolean check(int x , int y){
			if(x > 9 || x < 0 || y > 9 || y < 0)return false;
			return true;
		}
}

