public class Main {

	/***  */
	private static int[][] paper;
	/*** ,, */
	private static final int S = 1, M = 2, L = 3;
	/***  */
	private static BufferedReader br;
	/***  */
	private static int plus = 2;
	/***  */
	private static int normal = 10;
	/*** , , , ,  */
	private static int x, y, size, blindNum, max;

	/***  */
	static {
		int extention = normal + plus * plus; // 
		paper = new int[extention][extention];
		br = new BufferedReader(new InputStreamReader(System.in));
	}

	/**
	 * 
	 */
	public static void main(String[] args) throws IOException {

		String line; // 

		while ((line = br.readLine()) != null && !line.isEmpty()) {
			setInput(line);
		}
		calcu();
		System.out.println(blindNum + "\n" + max);
	}

	/**
	 * 
	 */
	private static void setInput(String line) {
		StringTokenizer st = new StringTokenizer(line, ","); // 
		x = Integer.parseInt(st.nextToken()) + plus; // 
		y = Integer.parseInt(st.nextToken()) + plus; // 
		size = Integer.parseInt(st.nextToken());
		set(x, y, size); // 
	}

	/**
	 * 
	 */
	private static void calcu() {
		for (x = plus; x < normal + plus; x++) {
			for (y = plus; y < normal + plus; y++) {
				if (paper[x][y] == 0) // 
					blindNum++;
				if (max < paper[x][y]) // 
					max = paper[x][y];
			}
		}
	}

	/**
	 * 
	 */
	private static void set(int x, int y, int size) {
		paper[x][y]++;
		if (size >= S) {
			paper[x - 1][y]++;
			paper[x + 1][y]++;
			paper[x][y - 1]++;
			paper[x][y + 1]++;
		}
		if (size >= M) {
			paper[x - 1][y - 1]++;
			paper[x - 1][y + 1]++;
			paper[x + 1][y - 1]++;
			paper[x + 1][y + 1]++;
		}
		if (size >= L) {
			paper[x - 2][y]++;
			paper[x + 2][y]++;
			paper[x][y - 2]++;
			paper[x][y + 2]++;
		}
	}
}

