import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

public class Main {
	private static final byte MASK_RIGHT = 0x0F;
	private static int[][] paper = new int[10][10];

	public static void main(String[] args) throws NumberFormatException, IOException {
		BufferedReader reader = new BufferedReader(new InputStreamReader(System.in), 128);
		String sLine;
		while ((sLine = reader.readLine()) != null) {
			droppingInk(sLine.charAt(0) & MASK_RIGHT, sLine.charAt(2) & MASK_RIGHT, sLine.charAt(4) & MASK_RIGHT);
		}
		calcResult();
	}

	private static void droppingInk(int x, int y, int size) {
		staining(x, y - 1);
		staining(x - 1, y);
		staining(x, y);
		staining(x + 1, y);
		staining(x, y + 1);
		if (size == 1) {
			return;
		}

		staining(x - 1, y - 1);
		staining(x + 1, y - 1);
		staining(x - 1, y + 1);
		staining(x + 1, y + 1);
		if (size == 2) {
			return;
		}

		staining(x, y - 2);
		staining(x - 2, y);
		staining(x + 2, y);
		staining(x, y + 2);
	}

	private static void staining(int x, int y) {
		if (x >= 0 && x < 10 && y >= 0 && y < 10) {
			paper[x][y]++;
		}
	}

	private static void calcResult() {
		int white = 0;
		int maxnoud = 0;
		for (int[] row : paper) {
			for (int colum : row) {
				if (colum == 0) {
					white++;
				} else if (colum > maxnoud) {
					maxnoud = colum;
				}
			}
		}
		System.out.println(white + System.getProperty("line.separator") + maxnoud);
	}
}

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.ArrayList;
import java.util.List;
import java.util.StringTokenizer;

public class Main {

	// 
	static int droppingField[][] = new int[10][10];
	// 
	static int clearCount = 0;
	// 
	static int maxDoropping = 0;

	public static void main(String[] args) throws NumberFormatException,
			IOException {

		// 
		BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
		List<int[]> inkList = new ArrayList<int[]>();

		// 
		String line = "";
		while ((line = br.readLine()) != null) {

			// 1
			StringTokenizer st = new StringTokenizer(line, ",");

			// 
			while (st.hasMoreTokens()) {
				int[] input = { Integer.parseInt(st.nextToken()),
						Integer.parseInt(st.nextToken()),
						Integer.parseInt(st.nextToken()) };
				inkList.add(input);
			}
		}
		br.close();

		// 
		drop(inkList, droppingField);

		//  
		outputResult();

		//  
		System.out.println(clearCount);

	System.out.println(maxDoropping);

	}

	// 
	public static void drop(List<int[]> inkList, int[][] droppingField) {

		List<int[]> inks = new ArrayList<int[]>();
		int maxLength = droppingField.length;

		// 
		for (int[] a : inkList) {

			if (a[2] == 1) {
				inks.addAll(smallInk(a[0], a[1], maxLength));
			} else if (a[2] == 2) {
				inks.addAll(middiumInk(a[0], a[1], maxLength));
			} else if (a[2] == 3) {
				inks.addAll(largeInk(a[0], a[1], maxLength));
			}

		}
		droppingInk(inks);

	}

	// 
	public static List<int[]> smallInk(int x, int y, int maxLength) {
		List<int[]> inks = new ArrayList<int[]>();

		int[] ink1 = { x, y };
		int[] ink2 = { x - 1, y };
		int[] ink3 = { x + 1, y };
		int[] ink4 = { x, y - 1 };
		int[] ink5 = { x, y + 1 };

		inks.add(ink1);
		inks.add(ink2);
		inks.add(ink3);
		inks.add(ink4);
		inks.add(ink5);

		return inks;
	}

	// 
	public static List<int[]> middiumInk(int x, int y, int maxLength) {
		List<int[]> inks = new ArrayList<int[]>();

		for (int i = x - 1; i <= x + 1; i++) {
			for (int n = y - 1; n <= y + 1; n++) {
				int[] ink1 = { i, n };
				inks.add(ink1);
			}
		}

		return inks;

	}

	// 
	public static List<int[]> largeInk(int x, int y, int maxLength) {
		List<int[]> inks = new ArrayList<int[]>();

		for (int i = x - 1; i <= x + 1; i++) {
			for (int n = y - 1; n <= y + 1; n++) {
				int[] ink1 = { i, n };
				inks.add(ink1);
			}
		}
		int[] ink2 = { x - 2, y };
		int[] ink3 = { x + 2, y };
		int[] ink4 = { x, y - 2 };
		int[] ink5 = { x, y + 2 };

		inks.add(ink2);
		inks.add(ink3);
		inks.add(ink4);
		inks.add(ink5);

		return inks;
	}

	// 
	public static void droppingInk(List<int[]> inks) {
		int maxLength = droppingField.length;

		for (int[] a : inks) {
			if (a[0] >= 0 && a[0] < maxLength && a[1] >= 0 && a[1] < maxLength) {
				droppingField[a[0]][a[1]]++;
			}
		}

	}

	//  
	public static void outputResult() {


		for (int i = 0; i < droppingField.length; i++) {
			for (int n = 0; n < droppingField[0].length; n++) {

				if (droppingField[i][n] == 0) {
					// (0)
					clearCount++;
				} else {
					// 
					maxDoropping = Math.max(maxDoropping, droppingField[i][n]);
				}

	}
		}

	}

}

