		ink(x,y);
		ink(x+1,y);
		ink(x-1,y);
		ink(x,y+1);
		ink(x,y-1);
	}

	static void inkM(int x, int y) {
		inkS(x,y);
		ink(x+1,y+1);
		ink(x+1,y-1);
		ink(x-1,y+1);
		ink(x-1,y-1);
	}

