            {
                int[] temp = Array.ConvertAll(input.Split(','), int.Parse);
                int x = temp[0];
                int y = temp[1];
                int s = temp[2];

                Paint(map, x, y, s);
            }

            int count = 0, max = 0;

            for (int i = 2; i < 12; i++)
            {
                for (int j = 2; j < 12; j++)
                {
                    if (map[i, j] == 0) count++;

                    max = Math.Max(max, map[i, j]);
                }
            }
            Console.WriteLine(count + "\n" + max);
        }

        static void Paint(int[,] map, int x, int y, int size)
        {
            x += 2; y += 2;

            map[y, x]++;

            map[y, x - 1]++; map[y, x + 1]++;
            map[y - 1, x]++; map[y + 1, x]++;

            if (size == 1) return;

            map[y - 1, x - 1]++; map[y - 1, x + 1]++;
            map[y + 1, x - 1]++; map[y + 1, x + 1]++;

            if (size == 2) return;

            map[y, x - 2]++; map[y, x + 2]++;
            map[y - 2, x]++; map[y + 2, x]++;
        }
    }
}

