	private static void DropInk(int x, int y, int size, int[,] p)
	{
		if(size >= 1) {
			p[x,y]++;

			if(x != 0) p[x-1, y]++;
			if(y != 0) p[x, y-1]++;
			if(x != 9) p[x+1, y]++;
			if(y != 9) p[x, y+1]++;
		}

		if(size >= 2) {
			if(x != 0 && y != 0) p[x-1, y-1]++;
			if(x != 0 && y != 9) p[x-1, y+1]++;
			if(x != 9 && y != 0) p[x+1, y-1]++;
			if(x != 9 && y != 9) p[x+1, y+1]++;
		}

		if(size >= 3) {
			if(x >= 2)	p[x-2, y]++;
			if(y >= 2)	p[x, y-2]++;
			if(x <= 7) 	p[x+2, y]++;
			if(y <= 7) 	p[x, y+2]++;
