        {
            InkMidium(ref sheet, x, y);
            if (x <= 1)
            {
                if (y <= 1)
                {
                    sheet[x][y + 2] += 1;
                    sheet[x + 2][y] += 1;
                }
                else if (y >= 8)
                {
                    sheet[x][y - 2] += 1;
                    sheet[x + 2][y] += 1;
                }
                else
                {
                    sheet[x][y - 2] += 1;
                    sheet[x][y + 2] += 1;
                    sheet[x + 2][y] += 1;
                }
