        internal void InkMidium(ref int[][] sheet, int x, int y)
        {
            InkSmall(ref sheet, x, y);
            if (x == 0)
            {
                if (y == 0)
                {
                    sheet[x + 1][y + 1] += 1;
                }
                else if (y == 9)
                {
                    sheet[x + 1][y - 1] += 1;
                }
                else
                {
                    sheet[x + 1][y - 1] += 1;
                    sheet[x + 1][y + 1] += 1;
                }
            }
            else if (x == 9)
            {
                if (y == 0)
                {
                    sheet[x - 1][y + 1] += 1;
                }
                else if (y == 9)
                {
                    sheet[x + 1][y - 1] += 1;
                }
                else
                {
                    sheet[x - 1][y - 1] += 1;
                    sheet[x - 1][y + 1] += 1;
                }
            }
            else if (y == 0)
            {
                sheet[x - 1][y + 1] += 1;
