            {
                sheet[x - 1][y - 1] += 1;
                sheet[x + 1][y - 1] += 1;
            }
            else
            {
                sheet[x - 1][y - 1] += 1;
                sheet[x + 1][y - 1] += 1;
                sheet[x - 1][y + 1] += 1;
                sheet[x + 1][y + 1] += 1;
            }
        }
        internal void InkSmall(ref int[][] sheet, int x, int y)
        {
            if (x == 0)
            {
                if (y == 0)
                {
                    sheet[x][y + 1] += 1;
                    sheet[x][y] += 1;
                    sheet[x + 1][y] += 1;
                }
                else if (y == 9)
                {
                    sheet[x][y - 1] += 1;
                    sheet[x][y] += 1;
                    sheet[x + 1][y] += 1;
                }
                else
                {
                    sheet[x][y - 1] += 1;
                    sheet[x][y + 1] += 1;
                    sheet[x][y] += 1;
                    sheet[x + 1][y] += 1;
                }
            }
            else if (x == 9)
            {
                if (y == 0)
                {
                    sheet[x][y + 1] += 1;
                    sheet[x][y] += 1;
                    sheet[x - 1][y] += 1;
                }
                else if (y == 9)
                {
                    sheet[x][y - 1] += 1;
                    sheet[x][y] += 1;
                    sheet[x - 1][y] += 1;
                }
                else
                {
                    sheet[x][y - 1] += 1;
                    sheet[x][y + 1] += 1;
                    sheet[x][y] += 1;
                    sheet[x - 1][y] += 1;
                }
            }
            else if (y == 0)
            {
                sheet[x][y + 1] += 1;
                sheet[x][y] += 1;
                sheet[x - 1][y] += 1;
                sheet[x + 1][y] += 1;
            }
            else if (y == 9)
            {
                sheet[x][y - 1] += 1;
                sheet[x][y] += 1;
                sheet[x - 1][y] += 1;
                sheet[x + 1][y] += 1;
            }
            else
            {
                sheet[x][y - 1] += 1;
                sheet[x][y + 1] += 1;
                sheet[x][y] += 1;
                sheet[x - 1][y] += 1;
                sheet[x + 1][y] += 1;
            }
        }
    }
    class Calclation
    {
        internal int CountValueIs0In2D(int[][] sheet)
        {
            int zeroCounter = 0;
            for (int i = 0; i < sheet.Length; ++i)
            {
                for (int j = 0; j < sheet[i].Length; ++j)
                {
                    if (sheet[i][j] == 0)
                    {
                        ++zeroCounter;
                    }
                }
            }
            return zeroCounter;
        }
        internal int MaxIn2D(int[][] sheet)
        {
            int max = 0;
            for (int i = 0; i < sheet.Length; ++i)
            {
                for (int j = 0; j < sheet[i].Length; ++j)
                {
                    if (max < sheet[i][j])
                    {
                        max = sheet[i][j];
                    }
                }
            }
            return max;
        }
    }
