        {
            for (int j = 0; j < 10; j++)
                a[i, j] = 0;
        }

        int x,y,size;

        while (true)
        {
            string s = Console.ReadLine();
            if (s == null) break;

            string[] ss = s.Split(',');

            x = int.Parse(ss[0]);
            y = int.Parse(ss[1]);
            size = int.Parse(ss[2]);

            switch (size)
            {

                case 1:
                    if (y - 1 >= 0)
                        a[y - 1, x] += 1;
                    if (x - 1 >= 0)
                        a[y, x - 1] += 1;
                    a[y, x] += 1;
                    if (y + 1 <= 9)
                        a[y + 1, x] += 1;
                    if (x + 1 <= 9)
                        a[y, x + 1] += 1;

                    break;

                case 2:
                    for (int i = x - 1; i <= x + 1; i++)
                    {
                        for (int j = y - 1; j <= y + 1; j++)
                        {
                            if (i >= 0 && j >= 0 && i <= 9 && j <= 9)
                                a[j, i] += 1;
                        }
                    }
                    break;

                case 3:
                    if (y - 2 >= 0)
                        a[y - 2, x] += 1;
                    if (x - 2 >= 0)
                        a[y, x - 2] += 1;

                    for (int i = x - 1; i <= x + 1; i++)
                    {
                        for (int j = y - 1; j <= y + 1; j++)
                        {
                            if (i >= 0 && j >= 0 && i <= 9 && j <= 9)
                                a[j, i] += 1;
                        }
                    }

                    if (y + 2 <= 9)
                        a[y + 2, x] += 1;
                    if (x + 2 <= 9)
                        a[y, x + 2] += 1;
                    break;
            }
