namespace aizu
{
    class OnlineJudge
    {
        static Dictionary<string, int> paper = new Dictionary<string, int>(200);
        static void Main()
        {
            string input;
            while((input = Console.ReadLine()) != null){
                Drop(input.Split(',').Select(d => int.Parse(d)).ToArray());
            }
            int[] item = (from q in paper select q.Value).ToArray();
            Console.WriteLine(100 - item.Count());
            Console.WriteLine(item.Max());
        }
        static void Drop(int[] input)
        {
            switch(input[2]) {
                case 3:
                    PaperInc(input[0] - 2, input[1]);
                    PaperInc(input[0] + 2, input[1]);
                    PaperInc(input[0], input[1] - 2);
                    PaperInc(input[0], input[1] + 2);
                    goto case 2;
                case 2:
                    PaperInc(input[0] - 1, input[1] + 1);
                    PaperInc(input[0] - 1, input[1] - 1);
                    PaperInc(input[0] + 1, input[1] + 1);
                    PaperInc(input[0] + 1, input[1] - 1);
                    goto case 1;
                case 1:
                    PaperInc(input[0] - 1, input[1]);
                    PaperInc(input[0] + 1, input[1]);
                    PaperInc(input[0], input[1] - 1);
                    PaperInc(input[0], input[1] + 1);
                    PaperInc(input[0], input[1]);
                    break;
            }
        }
        static void PaperInc(int x, int y)
        {
            if(x >= 0 && x < 10 && y >= 0 && y < 10) {
                string point = x + " " + y;
                if(!paper.ContainsKey(point)){
                    paper.Add(point, 0);
                }
                ++paper[point];
            }
        }
    }
}

