        self.point(x + 1, y - 1)
        self.point(x - 1, y - 1)
    def large(self, x, y):
        if not self.check(x, y):
            return
        self.medium(x, y)
        self.point(x, y + 2)
        self.point(x - 2, y)
        self.point(x + 2, y)
        self.point(x, y - 2)
