class Cloth(object):
    cell = None
    def __init__(self, x, y):
        self.cell = list([list([0 for i in range(y)]) for i in range(x)])
    def point(self, x, y):
        try:
            self.cell[x][y] += 1
        except IndexError:
            pass
    def small(self, x, y):
        self.point(x, y + 1)
        self.point(x - 1, y)
        self.point(x, y)
        self.point(x + 1, y)
        self.point(x, y - 1)
    def medium(self, x, y):
        self.small(x, y)
        self.point(x + 1, y + 1)
        self.point(x - 1, y + 1)
        self.point(x + 1, y - 1)
        self.point(x - 1, y - 1)
    def large(self, x, y):
        self.medium(x, y)
        self.point(x, y + 2)
        self.point(x - 2, y)
        self.point(x + 2, y)
        self.point(x, y - 2)
